// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

/**
 * The purpose of this contract is to hold BaseFeeLMA tokens for gas compensation:
 * https://github.com/liquity/dev#gas-compensation
 * When a borrower opens a trove, an additional 50 BaseFeeLMA debt is issued,
 * and 50 BaseFeeLMA is minted and sent to this contract.
 * When a borrower closes their active trove, this gas compensation is refunded:
 * 50 BaseFeeLMA is burned from the this contract's balance, and the corresponding
 * 50 BaseFeeLMA debt on the trove is cancelled.
 * See this issue for more context: https://github.com/liquity/dev/issues/186
 */
contract GasPool {
    // do nothing, as the core contracts have permission to send to and burn from this address
}

contracts/HintHelpers.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "./interfaces/ITroveManager.sol";
import "./interfaces/ISortedTroves.sol";
import "./dependencies/HedgehogBase.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./dependencies/CheckContract.sol";

contract HintHelpers is HedgehogBase, Ownable, CheckContract {
    using SafeMath for uint256;
    string public constant NAME = "HintHelpers";

    ISortedTroves public sortedTroves;
    ITroveManager public troveManager;

    // --- Events ---

    event SortedTrovesAddressChanged(address _sortedTrovesAddress);
    event TroveManagerAddressChanged(address _troveManagerAddress);

    // --- Dependency setters ---

    function setAddresses(
        address _sortedTrovesAddress,
        address _troveManagerAddress
    ) external onlyOwner {
        checkContract(_sortedTrovesAddress);
        checkContract(_troveManagerAddress);

        sortedTroves = ISortedTroves(_sortedTrovesAddress);
        troveManager = ITroveManager(_troveManagerAddress);

        emit SortedTrovesAddressChanged(_sortedTrovesAddress);
        emit TroveManagerAddressChanged(_troveManagerAddress);

        renounceOwnership();
    }

    // --- Functions ---

    /* getRedemptionHints() - Helper function for finding the right hints to pass to redeemCollateral().
     *
     * It simulates a redemption of `_BaseFeeLMAamount` to figure out where the redemption sequence will start and what state the final Trove
     * of the sequence will end up in.
     *
     * Returns three hints:
     *  - `firstRedemptionHint` is the address of the first Trove with ICR >= MCR (i.e. the first Trove that will be redeemed).
     *  - `partialRedemptionHintNICR` is the final nominal ICR of the last Trove of the sequence after being hit by partial redemption,
     *     or zero in case of no partial redemption.
     *  - `truncatedBaseFeeLMAamount` is the maximum amount that can be redeemed out of the the provided `_BaseFeeLMAamount`. This can be lower than
     *    `_BaseFeeLMAamount` when redeeming the full amount would leave the last Trove of the redemption sequence with less net debt than the
     *    minimum allowed value (i.e. MIN_NET_DEBT).
     *
     * The number of Troves to consider for redemption can be capped by passing a non-zero value as `_maxIterations`, while passing zero
     * will leave it uncapped.
     */

    function getRedemptionHints(
        uint _BaseFeeLMAamount,
        uint _price,
        uint _maxIterations
    )
        external
        view
        returns (
            address firstRedemptionHint,
            uint partialRedemptionHintNICR,
            uint truncatedBaseFeeLMAamount
        )
    {
        ISortedTroves sortedTrovesCached = sortedTroves;

        uint remainingBaseFeeLMA = _BaseFeeLMAamount;
        address currentTroveuser = sortedTrovesCached.getLast();

        while (
            currentTroveuser != address(0) &&
            troveManager.getCurrentICR(currentTroveuser, _price) < MCR
        ) {
            currentTroveuser = sortedTrovesCached.getPrev(currentTroveuser);
        }

        firstRedemptionHint = currentTroveuser;

        if (_maxIterations == 0) {
            // Previous implementation: _maxIterations = uint(-1);
            // Updated since 8th pragma does not allow anymore
            _maxIterations = type(uint).max;
        }

        while (
            currentTroveuser != address(0) &&
            remainingBaseFeeLMA > 0 &&
            _maxIterations-- > 0
        ) {
            uint netBaseFeeLMADebt = _getNetDebt(
                troveManager.getTroveDebt(currentTroveuser)
            ).add(
                    troveManager.getPendingBaseFeeLMADebtReward(
                        currentTroveuser
                    )
                );

            if (netBaseFeeLMADebt > remainingBaseFeeLMA) {
                if (netBaseFeeLMADebt > MIN_NET_DEBT) {
                    uint maxRedeemableBaseFeeLMA = LiquityMath._min(
                        remainingBaseFeeLMA,
                        netBaseFeeLMADebt.sub(MIN_NET_DEBT)
                    );

                    uint StETH = troveManager
                        .getTroveColl(currentTroveuser)
                        .add(
                            troveManager.getPendingStETHReward(currentTroveuser)
                        );

                    uint newColl = StETH.sub(
                        maxRedeemableBaseFeeLMA.mul(DECIMAL_PRECISION).div(
                            _price
                        )
                    );
                    uint newDebt = netBaseFeeLMADebt.sub(
                        maxRedeemableBaseFeeLMA
                    );

                    uint compositeDebt = _getCompositeDebt(newDebt);
                    partialRedemptionHintNICR = LiquityMath._computeNominalCR(
                        newColl,
                        compositeDebt
                    );

                    remainingBaseFeeLMA = remainingBaseFeeLMA.sub(
                        maxRedeemableBaseFeeLMA
                    );
                }
                break;
            } else {
                remainingBaseFeeLMA = remainingBaseFeeLMA.sub(
                    netBaseFeeLMADebt
                );
            }

            currentTroveuser = sortedTrovesCached.getPrev(currentTroveuser);
        }

        truncatedBaseFeeLMAamount = _BaseFeeLMAamount.sub(remainingBaseFeeLMA);
    }

    /* getApproxHint() - return address of a Trove that is, on average, (length / numTrials) positions away in the 
    sortedTroves list from the correct insert position of the Trove to be inserted. 

    Note: The output address is worst-case O(n) positions away from the correct insert position, however, the function 
    is probabilistic. Input can be tuned to guarantee results to a high degree of confidence, e.g:

    Submitting numTrials = k * sqrt(length), with k = 15 makes it very, very likely that the ouput address will 
    be <= sqrt(length) positions away from the correct insert position.
    */
    function getApproxHint(
        uint _CR,
        uint _numTrials,
        uint _inputRandomSeed
    )
        external
        view
        returns (address hintAddress, uint diff, uint latestRandomSeed)
    {
        uint arrayLength = troveManager.getTroveOwnersCount();

        if (arrayLength == 0) {
            return (address(0), 0, _inputRandomSeed);
        }

        hintAddress = sortedTroves.getLast();
        diff = LiquityMath._getAbsoluteDifference(
            _CR,
            troveManager.getNominalICR(hintAddress)
        );
        latestRandomSeed = _inputRandomSeed;

        uint i = 1;

        while (i < _numTrials) {
            latestRandomSeed = uint(
                keccak256(abi.encodePacked(latestRandomSeed))
            );

            uint arrayIndex = latestRandomSeed % arrayLength;
            address currentAddress = troveManager.getTroveFromTroveOwnersArray(
                arrayIndex
            );
            uint currentNICR = troveManager.getNominalICR(currentAddress);

            // check if abs(current - CR) > abs(closest - CR), and update closest if current is closer
            uint currentDiff = LiquityMath._getAbsoluteDifference(
                currentNICR,
                _CR
            );

            if (currentDiff < diff) {
                diff = currentDiff;
                hintAddress = currentAddress;
            }
            i++;
        }
    }

    function computeNominalCR(
        uint _coll,
        uint _debt
    ) external pure returns (uint) {
        return LiquityMath._computeNominalCR(_coll, _debt);
    }

    function computeCR(
        uint _coll,
        uint _debt,
        uint _price
    ) external pure returns (uint) {
        return LiquityMath._computeCR(_coll, _debt, _price);
    }
}

contracts/HOG/CommunityIssuance.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../interfaces/IHOGToken.sol";
import "../interfaces/ICommunityIssuance.sol";
import "../dependencies/BaseMath.sol";
import "../dependencies/LiquityMath.sol";
import "../dependencies/CheckContract.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract CommunityIssuance is Ownable, CheckContract, BaseMath {
    using SafeMath for uint;

    // --- Data ---

    string public constant NAME = "CommunityIssuance";

    uint public constant SECONDS_IN_ONE_MINUTE = 60;

    /* The issuance factor F determines the curvature of the issuance curve.
     *
     * Minutes in one year: 60*24*365 = 525600
     *
     * For 50% of remaining tokens issued each year, with minutes as time units, we have:
     *
     * F ** 525600 = 0.5
     *
     * Re-arranging:
     *
     * 525600 * ln(F) = ln(0.5)
     * F = 0.5 ** (1/525600)
     * F = 0.999998681227695000
     */
    uint public constant ISSUANCE_FACTOR = 999998681227695000;

    /*
     * The community HOG supply cap is the starting balance of the Community Issuance contract.
     * It should be minted to this contract by HOGToken, when the token is deployed.
     *
     * Set to 32M (slightly less than 1/3) of total HOG supply.
     */
    uint public constant HOGSupplyCap = 32e24; // 32 million

    IHOGToken public hogToken;

    address public stabilityPoolAddress;

    uint public totalHOGIssued;
    uint public immutable deploymentTime;

    // --- Events ---

    event HOGTokenAddressSet(address _hogTokenAddress);
    event StabilityPoolAddressSet(address _stabilityPoolAddress);
    event TotalHOGIssuedUpdated(uint _totalHOGIssued);

    // --- Functions ---

    constructor() {
        deploymentTime = block.timestamp;
    }

    function setAddresses(
        address _hogTokenAddress,
        address _stabilityPoolAddress
    ) external onlyOwner {
        checkContract(_hogTokenAddress);
        checkContract(_stabilityPoolAddress);

        hogToken = IHOGToken(_hogTokenAddress);
        stabilityPoolAddress = _stabilityPoolAddress;

        // When HOGToken deployed, it should have transferred CommunityIssuance's HOG entitlement
        uint HOGBalance = hogToken.balanceOf(address(this));
        require(HOGBalance >= HOGSupplyCap, "CI: Ballance is not enough");

        emit HOGTokenAddressSet(_hogTokenAddress);
        emit StabilityPoolAddressSet(_stabilityPoolAddress);

        renounceOwnership();
    }

    function issueHOG() external returns (uint) {
        _requireCallerIsStabilityPool();

        uint latestTotalHOGIssued = HOGSupplyCap
            .mul(_getCumulativeIssuanceFraction())
            .div(DECIMAL_PRECISION);
        uint issuance = latestTotalHOGIssued.sub(totalHOGIssued);

        totalHOGIssued = latestTotalHOGIssued;
        emit TotalHOGIssuedUpdated(latestTotalHOGIssued);

        return issuance;
    }

    /* Gets 1-f^t    where: f < 1

    f: issuance factor that determines the shape of the curve
    t:  time passed since last HOG issuance event  */
    function _getCumulativeIssuanceFraction() internal view returns (uint) {
        // Get the time passed since deployment
        uint timePassedInMinutes = block.timestamp.sub(deploymentTime).div(
            SECONDS_IN_ONE_MINUTE
        );

        // f^t
        uint power = LiquityMath._decPow(ISSUANCE_FACTOR, timePassedInMinutes);

        //  (1 - f^t)
        uint cumulativeIssuanceFraction = (uint(DECIMAL_PRECISION).sub(power));
        assert(cumulativeIssuanceFraction <= DECIMAL_PRECISION); // must be in range [0,1]

        return cumulativeIssuanceFraction;
    }

    function sendHOG(address _account, uint _HOGamount) external {
        _requireCallerIsStabilityPool();

        hogToken.transfer(_account, _HOGamount);
    }

    // --- 'require' functions ---

    function _requireCallerIsStabilityPool() internal view {
        require(
            msg.sender == stabilityPoolAddress,
            "CommunityIssuance: caller is not SP"
        );
    }
}

contracts/HOG/HOGStaking.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../dependencies/BaseMath.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../dependencies/CheckContract.sol";
import "../interfaces/IHOGToken.sol";
import "../interfaces/IHOGStaking.sol";
import "../dependencies/LiquityMath.sol";
import "../interfaces/IBaseFeeLMAToken.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "hardhat/console.sol";

contract HOGStaking is Ownable, CheckContract, BaseMath {
    using SafeMath for uint;
    using SafeERC20 for IERC20;

    // --- Data ---
    string public constant NAME = "HOGStaking";

    mapping(address => uint) public stakes;
    uint public totalHOGStaked;
    IERC20 StETHToken;

    uint public F_StETH; // Running sum of StETH fees per-HOG-staked
    uint public F_BaseFeeLMA; // Running sum of HOG fees per-HOG-staked

    // User snapshots of F_StETH and F_BaseFeeLMA, taken at the point at which their latest deposit was made
    mapping(address => Snapshot) public snapshots;

    struct Snapshot {
        uint F_StETH_Snapshot;
        uint F_BaseFeeLMA_Snapshot;
    }

    IHOGToken public hogToken;
    IBaseFeeLMAToken public baseFeeLMAToken;

    address public troveManagerAddress;
    address public borrowerOperationsAddress;
    address public activePoolAddress;

    // --- Events ---

    event HOGTokenAddressSet(address _hogTokenAddress);
    event BaseFeeLMATokenAddressSet(address _baseFeeLMATokenAddress);
    event TroveManagerAddressSet(address _troveManager);
    event BorrowerOperationsAddressSet(address _borrowerOperationsAddress);
    event ActivePoolAddressSet(address _activePoolAddress);
    event StETHTokenAddressUpdated(IERC20 _StEthAddress);

    event StakeChanged(address indexed staker, uint newStake);
    event StakingGainsWithdrawn(
        address indexed staker,
        uint BaseFeeLMAGain,
        uint StETHGain
    );
    event F_StETHUpdated(uint _F_StETH);
    event F_BaseFeeLMAUpdated(uint _F_BaseFeeLMA);
    event TotalHOGStakedUpdated(uint _totalHOGStaked);
    event StETHSent(address _account, uint _amount);
    event StakerSnapshotsUpdated(
        address _staker,
        uint _F_StETH,
        uint _F_BaseFeeLMA
    );

    // --- Functions ---

    function setAddresses(
        address _hogTokenAddress,
        address _baseFeeLMATokenAddress,
        address _troveManagerAddress,
        address _borrowerOperationsAddress,
        address _activePoolAddress,
        IERC20 _stETHTokenAddress
    ) external onlyOwner {
        checkContract(_hogTokenAddress);
        checkContract(_baseFeeLMATokenAddress);
        checkContract(_troveManagerAddress);
        checkContract(_borrowerOperationsAddress);
        checkContract(_activePoolAddress);
        checkContract(address(_stETHTokenAddress));

        hogToken = IHOGToken(_hogTokenAddress);
        baseFeeLMAToken = IBaseFeeLMAToken(_baseFeeLMATokenAddress);
        troveManagerAddress = _troveManagerAddress;
        borrowerOperationsAddress = _borrowerOperationsAddress;
        activePoolAddress = _activePoolAddress;
        StETHToken = _stETHTokenAddress;

        emit HOGTokenAddressSet(_hogTokenAddress);
        emit HOGTokenAddressSet(_baseFeeLMATokenAddress);
        emit TroveManagerAddressSet(_troveManagerAddress);
        emit BorrowerOperationsAddressSet(_borrowerOperationsAddress);
        emit ActivePoolAddressSet(_activePoolAddress);
        emit StETHTokenAddressUpdated(_stETHTokenAddress);

        renounceOwnership();
    }

    // If caller has a pre-existing stake, send any accumulated StETH and BaseFeeLMA gains to them.
    function stake(uint _HOGamount) external {
        _requireNonZeroAmount(_HOGamount);

        uint currentStake = stakes[msg.sender];

        uint StETHGain;
        uint BaseFeeLMAGain;
        // Grab any accumulated StETH and BaseFeeLMA gains from the current stake
        if (currentStake != 0) {
            StETHGain = _getPendingStETHGain(msg.sender);
            BaseFeeLMAGain = _getPendingBaseFeeLMAGain(msg.sender);
        }

        _updateUserSnapshots(msg.sender);

        uint newStake = currentStake.add(_HOGamount);

        // Increase users stake and total HOG staked
        stakes[msg.sender] = newStake;
        totalHOGStaked = totalHOGStaked.add(_HOGamount);
        emit TotalHOGStakedUpdated(totalHOGStaked);

        // Transfer HOG from caller to this contract
        hogToken.sendToHOGStaking(msg.sender, _HOGamount);

        emit StakeChanged(msg.sender, newStake);
        emit StakingGainsWithdrawn(msg.sender, BaseFeeLMAGain, StETHGain);

        // Send accumulated BaseFeeLMA and StETH gains to the caller
        if (currentStake != 0) {
            baseFeeLMAToken.transfer(msg.sender, BaseFeeLMAGain);
            _sendStETHGainToUser(StETHGain);
        }
    }

    // Unstake the HOG and send the it back to the caller, along with their accumulated BaseFeeLMA & StETH gains.
    // If requested amount > stake, send their entire stake.
    function unstake(uint _HOGamount) external {
        uint currentStake = stakes[msg.sender];
        _requireUserHasStake(currentStake);

        // Grab any accumulated StETH and BaseFeeLMA gains from the current stake
        uint StETHGain = _getPendingStETHGain(msg.sender);
        uint BaseFeeLMAGain = _getPendingBaseFeeLMAGain(msg.sender);

        _updateUserSnapshots(msg.sender);

        if (_HOGamount > 0) {
            uint HOGToWithdraw = LiquityMath._min(_HOGamount, currentStake);

            uint newStake = currentStake.sub(HOGToWithdraw);

            // Decrease user's stake and total HOG staked
            stakes[msg.sender] = newStake;
            totalHOGStaked = totalHOGStaked.sub(HOGToWithdraw);
            emit TotalHOGStakedUpdated(totalHOGStaked);

            // Transfer unstaked HOG to user
            hogToken.transfer(msg.sender, HOGToWithdraw);

            emit StakeChanged(msg.sender, newStake);
        }

        emit StakingGainsWithdrawn(msg.sender, BaseFeeLMAGain, StETHGain);

        // Send accumulated BaseFeeLMA and StETH gains to the caller
        baseFeeLMAToken.transfer(msg.sender, BaseFeeLMAGain);
        _sendStETHGainToUser(StETHGain);
    }

    // --- Reward-per-unit-staked increase functions. Called by Liquity core contracts ---

    function increaseF_StETH(uint _StETHFee) external {
        _requireCallerIsTroveManager();
        uint StETHFeePerHOGStaked;

        if (totalHOGStaked > 0) {
            StETHFeePerHOGStaked = _StETHFee.mul(DECIMAL_PRECISION).div(
                totalHOGStaked
            );
        }

        F_StETH = F_StETH.add(StETHFeePerHOGStaked);
        emit F_StETHUpdated(F_StETH);
    }

    function increaseF_BaseFeeLMA(uint _BaseFeeLMAFee) external {
        _requireCallerIsBorrowerOperations();
        uint BaseFeeLMAFeePerHOGStaked;

        if (totalHOGStaked > 0) {
            BaseFeeLMAFeePerHOGStaked = _BaseFeeLMAFee
                .mul(DECIMAL_PRECISION)
                .div(totalHOGStaked);
        }

        F_BaseFeeLMA = F_BaseFeeLMA.add(BaseFeeLMAFeePerHOGStaked);
        emit F_BaseFeeLMAUpdated(F_BaseFeeLMA);
    }

    // --- Pending reward functions ---

    function getPendingStETHGain(address _user) external view returns (uint) {
        return _getPendingStETHGain(_user);
    }

    function _getPendingStETHGain(address _user) internal view returns (uint) {
        uint F_StETH_Snapshot = snapshots[_user].F_StETH_Snapshot;
        uint StETHGain = stakes[_user].mul(F_StETH.sub(F_StETH_Snapshot)).div(
            DECIMAL_PRECISION
        );
        return StETHGain;
    }

    function getPendingBaseFeeLMAGain(
        address _user
    ) external view returns (uint) {
        return _getPendingBaseFeeLMAGain(_user);
    }

    function _getPendingBaseFeeLMAGain(
        address _user
    ) internal view returns (uint) {
        uint F_BaseFeeLMA_Snapshot = snapshots[_user].F_BaseFeeLMA_Snapshot;
        uint BaseFeeLMAGain = stakes[_user]
            .mul(F_BaseFeeLMA.sub(F_BaseFeeLMA_Snapshot))
            .div(DECIMAL_PRECISION);
        return BaseFeeLMAGain;
    }

    // --- Internal helper functions ---

    function _updateUserSnapshots(address _user) internal {
        snapshots[_user].F_StETH_Snapshot = F_StETH;
        snapshots[_user].F_BaseFeeLMA_Snapshot = F_BaseFeeLMA;
        emit StakerSnapshotsUpdated(_user, F_StETH, F_BaseFeeLMA);
    }

    /**
     * HEDGEHOG UPDATES: use SafeERC20 safe transfer instead of native token transfer
     */
    function _sendStETHGainToUser(uint StETHGain) internal {
        emit StETHSent(msg.sender, StETHGain);
        StETHToken.transfer(msg.sender, StETHGain);
    }

    // --- 'require' functions ---

    function _requireCallerIsTroveManager() internal view {
        require(
            msg.sender == troveManagerAddress,
            "HOGStaking: caller is not TroveM"
        );
    }

    function _requireCallerIsBorrowerOperations() internal view {
        require(
            msg.sender == borrowerOperationsAddress,
            "HOGStaking: caller is not BorrowerOps"
        );
    }

    function _requireCallerIsActivePool() internal view {
        require(
            msg.sender == activePoolAddress,
            "HOGStaking: caller is not ActivePool"
        );
    }

    function _requireUserHasStake(uint currentStake) internal pure {
        require(
            currentStake > 0,
            "HOGStaking: User must have a non-zero stake"
        );
    }

    function _requireNonZeroAmount(uint _amount) internal pure {
        require(_amount > 0, "HOGStaking: Amount must be non-zero");
    }

    receive() external payable {
        _requireCallerIsActivePool();
    }
}

contracts/HOG/HOGToken.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../dependencies/CheckContract.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "../interfaces/ILockupContractFactory.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../dependencies/IERC2612.sol";

/**
* @notice Token's functionality based on HOG token. 
* Functions logic remains unchanged.
 * Changes to the contract:
 * - Raised pragma version
 * - Removed an import of Token Interface
 * Even though SafeMath is no longer required, the decision was made to keep it to avoid human factor errors
*
* Based upon OpenZeppelin's ERC20 contract:
* https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/ERC20.sol
*  
* and their EIP2612 (ERC20Permit / ERC712) functionality:
* https://github.com/OpenZeppelin/openzeppelin-contracts/blob/53516bc555a454862470e7860a9b5254db4d00f5/contracts/token/ERC20/ERC20Permit.sol
* 
*
*  --- Functionality added specific to the HOG ---
* 
* 1) Transfer protection: blacklist of addresses that are invalid recipients (i.e. core Liquity contracts) in external 
* transfer() and transferFrom() calls. The purpose is to protect users from losing tokens by mistakenly sending HOG directly to a Liquity
* core contract, when they should rather call the right function.
*
* 2) sendToHOGStaking(): callable only by Hedgehog core contracts, which move HOG tokens from user -> HOGStaking contract.
*
* 3) Supply hard-capped at 100 million
*
* 4) CommunityIssuance and LockupContractFactory addresses are set at deployment
*
* 5) The bug bounties / hackathons allocation of 2 million tokens is minted at deployment to an EOA

* 6) 32 million tokens are minted at deployment to the CommunityIssuance contract
*
* 7) The LP rewards allocation of (1 + 1/3) million tokens is minted at deployent to a Staking contract
*
* 8) (64 + 2/3) million tokens are minted at deployment to the Hedgehog multisig
*
* 9) Until one year from deployment:
* -Hedgehog multisig may only transfer() tokens to LockupContracts that have been deployed via & registered in the 
*  LockupContractFactory 
* -approve(), increaseAllowance(), decreaseAllowance() revert when called by the multisig
* -transferFrom() reverts when the multisig is the sender
* -sendToHOGStaking() reverts when the multisig is the sender, blocking the multisig from staking its HOG.
* 
* After one year has passed since deployment of the HOGToken, the restrictions on multisig operations are lifted
* and the multisig has the same rights as any other address.
*/

contract HOGToken is CheckContract, IERC20, IERC2612 {
    using SafeMath for uint256;

    // --- ERC20 Data ---

    string internal constant _NAME = "Hedgehog";
    string internal constant _SYMBOL = "HOG";
    string internal constant _VERSION = "1";
    uint8 internal constant _DECIMALS = 18;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    uint private _totalSupply;

    // --- EIP 2612 Data ---

    // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
    bytes32 private constant _PERMIT_TYPEHASH =
        0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
    // keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)");
    bytes32 private constant _TYPE_HASH =
        0x8b73c3c69bb8fe3d512ecc4cf759cc79239f7b179b0ffacaa9a75d522b39400f;

    // Cache the domain separator as an immutable value, but also store the chain id that it corresponds to, in order to
    // invalidate the cached domain separator if the chain id changes.
    bytes32 private immutable _CACHED_DOMAIN_SEPARATOR;
    uint256 private immutable _CACHED_CHAIN_ID;

    bytes32 private immutable _HASHED_NAME;
    bytes32 private immutable _HASHED_VERSION;

    mapping(address => uint256) private _nonces;

    // --- HOGToken specific data ---

    uint public constant ONE_YEAR_IN_SECONDS = 31536000; // 60 * 60 * 24 * 365

    // uint for use with SafeMath
    uint internal _1_MILLION = 1e24; // 1e6 * 1e18 = 1e24

    uint internal immutable deploymentStartTime;
    address public immutable multisigAddress;

    address public immutable communityIssuanceAddress;
    address public immutable hogStakingAddress;

    uint internal immutable lpRewardsEntitlement;

    ILockupContractFactory public immutable lockupContractFactory;

    // --- Events ---

    event CommunityIssuanceAddressSet(address _communityIssuanceAddress);
    event HOGStakingAddressSet(address _hogStakingAddress);
    event LockupContractFactoryAddressSet(
        address _lockupContractFactoryAddress
    );

    // --- Functions ---

    constructor(
        address _communityIssuanceAddress,
        address _hogStakingAddress,
        address _lockupFactoryAddress,
        address _bountyAddress,
        address _lpRewardsAddress,
        address _multisigAddress
    ) {
        checkContract(_communityIssuanceAddress);
        checkContract(_hogStakingAddress);
        checkContract(_lockupFactoryAddress);
        // TODO: Pass addresses of all core contract to be able to restrict transfers to them
        multisigAddress = _multisigAddress;
        deploymentStartTime = block.timestamp;

        communityIssuanceAddress = _communityIssuanceAddress;
        hogStakingAddress = _hogStakingAddress;
        lockupContractFactory = ILockupContractFactory(_lockupFactoryAddress);

        bytes32 hashedName = keccak256(bytes(_NAME));
        bytes32 hashedVersion = keccak256(bytes(_VERSION));

        _HASHED_NAME = hashedName;
        _HASHED_VERSION = hashedVersion;
        _CACHED_CHAIN_ID = _chainID();
        _CACHED_DOMAIN_SEPARATOR = _buildDomainSeparator(
            _TYPE_HASH,
            hashedName,
            hashedVersion
        );

        // --- Initial HOG allocations ---

        uint bountyEntitlement = _1_MILLION.mul(2); // Allocate 2 million for bounties/hackathons
        _mint(_bountyAddress, bountyEntitlement);

        uint depositorsAndFrontEndsEntitlement = _1_MILLION.mul(32); // Allocate 32 million to the algorithmic issuance schedule
        _mint(_communityIssuanceAddress, depositorsAndFrontEndsEntitlement);

        uint _lpRewardsEntitlement = _1_MILLION.mul(4).div(3); // Allocate 1.33 million for LP rewards
        lpRewardsEntitlement = _lpRewardsEntitlement;
        _mint(_lpRewardsAddress, _lpRewardsEntitlement);

        // Allocate the remainder to the HOG Multisig: (100 - 2 - 32 - 1.33) million = 64.66 million
        uint multisigEntitlement = _1_MILLION
            .mul(100)
            .sub(bountyEntitlement)
            .sub(depositorsAndFrontEndsEntitlement)
            .sub(_lpRewardsEntitlement);

        _mint(_multisigAddress, multisigEntitlement);
    }

    // --- External functions ---

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(
        address account
    ) external view override returns (uint256) {
        return _balances[account];
    }

    function getDeploymentStartTime() external view returns (uint256) {
        return deploymentStartTime;
    }

    function getLpRewardsEntitlement() external view returns (uint256) {
        return lpRewardsEntitlement;
    }

    function transfer(
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        // Restrict the multisig's transfers in first year
        if (_callerIsMultisig() && _isFirstYear()) {
            _requireRecipientIsRegisteredLC(recipient);
        }

        _requireValidRecipient(recipient);

        // Otherwise, standard transfer functionality
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) external view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) external override returns (bool) {
        if (_isFirstYear()) {
            _requireCallerIsNotMultisig();
        }

        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) external override returns (bool) {
        if (_isFirstYear()) {
            _requireSenderIsNotMultisig(sender);
        }

        _requireValidRecipient(recipient);

        _transfer(sender, recipient, amount);
        _approve(
            sender,
            msg.sender,
            _allowances[sender][msg.sender].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) external returns (bool) {
        if (_isFirstYear()) {
            _requireCallerIsNotMultisig();
        }

        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) external returns (bool) {
        if (_isFirstYear()) {
            _requireCallerIsNotMultisig();
        }

        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function sendToHOGStaking(address _sender, uint256 _amount) external {
        _requireCallerIsHOGStaking();
        if (_isFirstYear()) {
            _requireSenderIsNotMultisig(_sender);
        } // Prevent the multisig from staking HOG
        _transfer(_sender, hogStakingAddress, _amount);
    }

    // --- EIP 2612 functionality ---

    function domainSeparator() public view override returns (bytes32) {
        if (_chainID() == _CACHED_CHAIN_ID) {
            return _CACHED_DOMAIN_SEPARATOR;
        } else {
            return
                _buildDomainSeparator(
                    _TYPE_HASH,
                    _HASHED_NAME,
                    _HASHED_VERSION
                );
        }
    }

    function permit(
        address owner,
        address spender,
        uint amount,
        uint deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external override {
        require(deadline >= block.timestamp, "HOG: expired deadline");
        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                domainSeparator(),
                keccak256(
                    abi.encode(
                        _PERMIT_TYPEHASH,
                        owner,
                        spender,
                        amount,
                        _nonces[owner]++,
                        deadline
                    )
                )
            )
        );
        address recoveredAddress = ecrecover(digest, v, r, s);
        require(recoveredAddress == owner, "HOG: invalid signature");
        _approve(owner, spender, amount);
    }

    function nonces(address owner) external view override returns (uint256) {
        // FOR EIP 2612
        return _nonces[owner];
    }

    // --- Internal operations ---

    function _chainID() private view returns (uint256 chainID) {
        assembly {
            chainID := chainid()
        }
    }

    function _buildDomainSeparator(
        bytes32 _typeHash,
        bytes32 _name,
        bytes32 _version
    ) private view returns (bytes32) {
        return
            keccak256(
                abi.encode(
                    _typeHash,
                    _name,
                    _version,
                    _chainID(),
                    address(this)
                )
            );
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        // TODO: Restrict access to all core contracts, unless msg.sender is another core contract
        _balances[sender] = _balances[sender].sub(
            amount,
            "ERC20: transfer amount exceeds balance"
        );
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }

    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "ERC20: mint to the zero address");

        _totalSupply = _totalSupply.add(amount);
        _balances[account] = _balances[account].add(amount);
        emit Transfer(address(0), account, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    // --- Helper functions ---

    function _callerIsMultisig() internal view returns (bool) {
        return (msg.sender == multisigAddress);
    }

    function _isFirstYear() internal view returns (bool) {
        return (block.timestamp.sub(deploymentStartTime) < ONE_YEAR_IN_SECONDS);
    }

    // --- 'require' functions ---

    function _requireValidRecipient(address _recipient) internal view {
        require(
            _recipient != address(0) && _recipient != address(this),
            "HOG: Cannot transfer tokens directly to the HOG token contract or the zero address"
        );
        require(
            _recipient != communityIssuanceAddress &&
                _recipient != hogStakingAddress,
            "HOG: Cannot transfer tokens directly to the community issuance or staking contract"
        );
    }

    function _requireRecipientIsRegisteredLC(address _recipient) internal view {
        require(
            lockupContractFactory.isRegisteredLockup(_recipient),
            "HOGToken: recipient must be a LockupContract registered in the Factory"
        );
    }

    function _requireSenderIsNotMultisig(address _sender) internal view {
        require(
            _sender != multisigAddress,
            "HOGToken: sender must not be the multisig"
        );
    }

    function _requireCallerIsNotMultisig() internal view {
        require(
            !_callerIsMultisig(),
            "HOGToken: caller must not be the multisig"
        );
    }

    function _requireCallerIsHOGStaking() internal view {
        require(
            msg.sender == hogStakingAddress,
            "HOGToken: caller must be the HOGStaking contract"
        );
    }

    // --- Optional functions ---

    function name() external pure returns (string memory) {
        return _NAME;
    }

    function symbol() external pure returns (string memory) {
        return _SYMBOL;
    }

    function decimals() external pure returns (uint8) {
        return _DECIMALS;
    }

    function version() external pure override returns (string memory) {
        return _VERSION;
    }

    function permitTypeHash() external pure override returns (bytes32) {
        return _PERMIT_TYPEHASH;
    }
}

