// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/IHOGToken.sol";

/*
* The lockup contract architecture utilizes a single LockupContract, with an unlockTime. The unlockTime is passed as an argument 
* to the LockupContract's constructor. The contract's balance can be withdrawn by the beneficiary when block.timestamp > unlockTime. 
* At construction, the contract checks that unlockTime is at least one year later than the Hedgehog system's deployment time. 

* Within the first year from deployment, the deployer of the HOGToken (Hedgehog AG's address) may transfer HOG only to valid 
* LockupContracts, and no other addresses (this is enforced in HOGToken.sol's transfer() function).
* 
* The above two restrictions ensure that until one year after system deployment, HOG tokens originating from Hedgehog AG cannot 
* enter circulating supply and cannot be staked to earn system revenue.
*/
contract LockupContract {
    using SafeMath for uint;

    // --- Data ---
    string public constant NAME = "LockupContract";

    uint public constant SECONDS_IN_ONE_YEAR = 31536000;

    address public immutable beneficiary;

    IHOGToken public hogToken;

    // Unlock time is the Unix point in time at which the beneficiary can withdraw.
    uint public unlockTime;

    // --- Events ---

    event LockupContractCreated(address _beneficiary, uint _unlockTime);
    event LockupContractEmptied(uint _HOGwithdrawal);

    // --- Functions ---

    constructor(
        address _hogTokenAddress,
        address _beneficiary,
        uint _unlockTime
    ) public {
        hogToken = IHOGToken(_hogTokenAddress);

        /*
         * Set the unlock time to a chosen instant in the future, as long as it is at least 1 year after
         * the system was deployed
         */
        _requireUnlockTimeIsAtLeastOneYearAfterSystemDeployment(_unlockTime);
        unlockTime = _unlockTime;

        beneficiary = _beneficiary;
        emit LockupContractCreated(_beneficiary, _unlockTime);
    }

    function withdrawHOG() external {
        _requireCallerIsBeneficiary();
        _requireLockupDurationHasPassed();

        IHOGToken hogTokenCached = hogToken;
        uint HOGBalance = hogTokenCached.balanceOf(address(this));
        hogTokenCached.transfer(beneficiary, HOGBalance);
        emit LockupContractEmptied(HOGBalance);
    }

    // --- 'require' functions ---

    function _requireCallerIsBeneficiary() internal view {
        require(
            msg.sender == beneficiary,
            "LockupContract: caller is not the beneficiary"
        );
    }

    function _requireLockupDurationHasPassed() internal view {
        require(
            block.timestamp >= unlockTime,
            "LockupContract: The lockup duration must have passed"
        );
    }

    function _requireUnlockTimeIsAtLeastOneYearAfterSystemDeployment(
        uint _unlockTime
    ) internal view {
        uint systemDeploymentTime = hogToken.getDeploymentStartTime();
        require(
            _unlockTime >= systemDeploymentTime.add(SECONDS_IN_ONE_YEAR),
            "LockupContract: unlock time must be at least one year after system deployment"
        );
    }
}

contracts/HOG/LockupContractFactory.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "../dependencies/CheckContract.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../interfaces/ILockupContractFactory.sol";
import "./LockupContract.sol";

/*
 * The LockupContractFactory deploys LockupContracts - its main purpose is to keep a registry of valid deployed
 * LockupContracts.
 *
 * This registry is checked by HOGToken when the Hedgehog deployer attempts to transfer HOG tokens. During the first year
 * since system deployment, the Hedgehog deployer is only allowed to transfer HOG to valid LockupContracts that have been
 * deployed by and recorded in the LockupContractFactory. This ensures the deployer's HOG can't be traded or staked in the
 * first year, and can only be sent to a verified LockupContract which unlocks at least one year after system deployment.
 *
 * LockupContracts can of course be deployed directly, but only those deployed through and recorded in the LockupContractFactory
 * will be considered "valid" by HOGToken. This is a convenient way to verify that the target address is a genuine
 * LockupContract.
 */

contract LockupContractFactory is Ownable, CheckContract {
    using SafeMath for uint;

    // --- Data ---
    string public constant NAME = "LockupContractFactory";

    uint public constant SECONDS_IN_ONE_YEAR = 31536000;

    address public hogTokenAddress;

    mapping(address => address) public lockupContractToDeployer;

    // --- Events ---

    event HOGTokenAddressSet(address _hogTokenAddress);
    event LockupContractDeployedThroughFactory(
        address _lockupContractAddress,
        address _beneficiary,
        uint _unlockTime,
        address _deployer
    );

    // --- Functions ---

    function setHOGTokenAddress(address _hogTokenAddress) external onlyOwner {
        checkContract(_hogTokenAddress);

        hogTokenAddress = _hogTokenAddress;
        emit HOGTokenAddressSet(_hogTokenAddress);

        renounceOwnership();
    }

    function deployLockupContract(
        address _beneficiary,
        uint _unlockTime
    ) external {
        address hogTokenAddressCached = hogTokenAddress;
        _requireHOGAddressIsSet(hogTokenAddressCached);
        LockupContract lockupContract = new LockupContract(
            hogTokenAddressCached,
            _beneficiary,
            _unlockTime
        );

        lockupContractToDeployer[address(lockupContract)] = msg.sender;
        emit LockupContractDeployedThroughFactory(
            address(lockupContract),
            _beneficiary,
            _unlockTime,
            msg.sender
        );
    }

    function isRegisteredLockup(
        address _contractAddress
    ) public view returns (bool) {
        return lockupContractToDeployer[_contractAddress] != address(0);
    }

    // --- 'require'  functions ---
    function _requireHOGAddressIsSet(address _hogTokenAddress) internal pure {
        require(_hogTokenAddress != address(0), "LCF: HOG Address is not set");
    }
}

