        address _deployer
    );

    // --- Functions ---

    function setHOGTokenAddress(address _hogTokenAddress) external;

    function deployLockupContract(
        address _beneficiary,
        uint _unlockTime
    ) external;

    function isRegisteredLockup(address _addr) external view returns (bool);
}

