    ) external returns (bool) {
        token.increaseAllowance(spender, addedValue);
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) external returns (bool) {
        token.decreaseAllowance(spender, subtractedValue);
    }
}

