// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

contract CheckContract {
    /**
     * Check that the account is an already deployed non-destroyed contract.
     * See: https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/utils/Address.sol#L12
     */
    function checkContract(address _account) internal view {
        require(_account != address(0), "Account cannot be zero address");

        uint256 size;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            size := extcodesize(_account)
        }
        require(size > 0, "Account code size cannot be zero");
    }
}

contracts/dependencies/HedgehogBase.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "./BaseMath.sol";
import "./LiquityMath.sol";
import "../interfaces/IActivePool.sol";
import "../interfaces/IDefaultPool.sol";
import "../interfaces/IPriceFeed.sol";
import "../interfaces/IHedgehogBase.sol";

/**
 * @notice Fork of LiquityMath with an upgraded pragma and:
 * Base contract for TroveManager, BorrowerOperations and StabilityPool. Contains global system constants and
 * common functions.
 *
 * Hedgehog updates:
 * Rename variables,
 * Increase MCR and CCR,
 * Update Min Net Debt
 */
contract HedgehogBase is BaseMath, IHedgehogBase {
    using SafeMath for uint;

    uint public constant _100pct = 1000000000000000000; // 1e18 == 100%

    // Minimum collateral ratio for individual troves
    uint public constant MCR = 1500000000000000000; // 150%

    // Critical system collateral ratio. If the system's total collateral ratio (TCR) falls below the CCR, Recovery Mode is triggered.
    uint public constant CCR = 1500000000000000000; // 150%

    // Amount of BaseFeeLMA to be locked in gas pool on opening troves
    uint public constant BaseFeeLMA_GAS_COMPENSATION = 50000;

    // Minimum amount of net BaseFeeLMA debt a trove must have
    uint public constant MIN_NET_DEBT = 1; // TODO: Update that value

    uint public constant PERCENT_DIVISOR = 200; // dividing by 200 yields 0.5%

    uint public constant BORROWING_FEE_FLOOR = (DECIMAL_PRECISION / 1000) * 5; // 0.5%

    IActivePool public activePool;

    IDefaultPool public defaultPool;

    IPriceFeed public override priceFeed;

    // --- Gas compensation functions ---

    // Returns the composite debt (drawn debt + gas compensation) of a trove, for the purpose of ICR calculation
    // HEDGEHOG UPDATES:
    // No longer deduct gas comp from a net debt
    function _getCompositeDebt(uint _debt) internal pure returns (uint) {
        return _debt;
    }

    // HEDGEHOG UPDATES:
    // No longer deduct gas comp from a net debt
    function _getNetDebt(uint _debt) internal pure returns (uint) {
        return _debt;
    }

    // Return the amount of StETH to be drawn from a trove's collateral and sent as gas compensation.
    function _getCollGasCompensation(
        uint _entireColl
    ) internal pure returns (uint) {
        return _entireColl / PERCENT_DIVISOR;
    }

    function getEntireSystemColl() public view returns (uint entireSystemColl) {
        uint activeColl = activePool.getStETH();
        uint liquidatedColl = defaultPool.getStETH();

        return activeColl.add(liquidatedColl);
    }

    function getEntireSystemDebt() public view returns (uint entireSystemDebt) {
        uint activeDebt = activePool.getBaseFeeLMADebt();
        uint closedDebt = defaultPool.getBaseFeeLMADebt();

        return activeDebt.add(closedDebt);
    }

    function _getTCR(uint _price) internal view returns (uint TCR) {
        uint entireSystemColl = getEntireSystemColl();
        uint entireSystemDebt = getEntireSystemDebt();

        TCR = LiquityMath._computeCR(
            entireSystemColl,
            entireSystemDebt,
            _price
        );

        return TCR;
    }

    function _checkRecoveryMode(uint _price) internal view returns (bool) {
        uint TCR = _getTCR(_price);

        return TCR < CCR;
    }

    function _requireUserAcceptsFee(
        uint _fee,
        uint _amount,
        uint _maxFeePercentage
    ) internal pure {
        uint feePercentage = _fee.mul(DECIMAL_PRECISION).div(_amount);
        require(
            feePercentage <= _maxFeePercentage,
            "Fee exceeded provided maximum"
        );
    }
}

contracts/dependencies/LiquityMath.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "hardhat/console.sol";

/**
 * @notice A fork of Liquity Math library with an upgraded pragma
 *
 * Even though SafeMath is no longer required, the decision was made to keep it to avoid human factor errors
 */

library LiquityMath {
    using SafeMath for uint;

    uint internal constant DECIMAL_PRECISION = 1e18;

    /* Precision for Nominal ICR (independent of price). Rationale for the value:
     *
     * - Making it “too high” could lead to overflows.
     * - Making it “too low” could lead to an ICR equal to zero, due to truncation from Solidity floor division.
     *
     * This value of 1e20 is chosen for safety: the NICR will only overflow for numerator > ~1e39 StETH,
     * and will only truncate to 0 if the denominator is at least 1e20 times greater than the numerator.
     *
     */
    uint internal constant NICR_PRECISION = 1e20;

    function _min(uint _a, uint _b) internal pure returns (uint) {
        return (_a < _b) ? _a : _b;
    }

    function _max(uint _a, uint _b) internal pure returns (uint) {
        return (_a >= _b) ? _a : _b;
    }

    /*
     * Multiply two decimal numbers and use normal rounding rules:
     * -round product up if 19'th mantissa digit >= 5
     * -round product down if 19'th mantissa digit < 5
     *
     * Used only inside the exponentiation, _decPow().
     */
    function decMul(uint x, uint y) internal pure returns (uint decProd) {
        uint prod_xy = x.mul(y);

        decProd = prod_xy.add(DECIMAL_PRECISION / 2).div(DECIMAL_PRECISION);
    }

    /*
     * _decPow: Exponentiation function for 18-digit decimal base, and integer exponent n.
     *
     * Uses the efficient "exponentiation by squaring" algorithm. O(log(n)) complexity.
     *
     * Called by two functions that represent time in units of minutes:
     * 1) TroveManager._calcDecayedBaseRate
     * 2) CommunityIssuance._getCumulativeIssuanceFraction
     *
     * The exponent is capped to avoid reverting due to overflow. The cap 525600000 equals
     * "minutes in 1000 years": 60 * 24 * 365 * 1000
     *
     * If a period of > 1000 years is ever used as an exponent in either of the above functions, the result will be
     * negligibly different from just passing the cap, since:
     *
     * In function 1), the decayed base rate will be 0 for 1000 years or > 1000 years
     * In function 2), the difference in tokens issued at 1000 years and any time > 1000 years, will be negligible
     */
    function _decPow(uint _base, uint _minutes) internal pure returns (uint) {
        if (_minutes > 525600000) {
            _minutes = 525600000;
        } // cap to avoid overflow

        if (_minutes == 0) {
            return DECIMAL_PRECISION;
        }

        uint y = DECIMAL_PRECISION;
        uint x = _base;
        uint n = _minutes;

        // Exponentiation-by-squaring
        while (n > 1) {
            if (n % 2 == 0) {
                x = decMul(x, x);
                n = n.div(2);
            } else {
                // if (n % 2 != 0)
                y = decMul(x, y);
                x = decMul(x, x);
                n = (n.sub(1)).div(2);
            }
        }

        return decMul(x, y);
    }

    function _getAbsoluteDifference(
        uint _a,
        uint _b
    ) internal pure returns (uint) {
        return (_a >= _b) ? _a.sub(_b) : _b.sub(_a);
    }

    function _computeNominalCR(
        uint _coll,
        uint _debt
    ) internal pure returns (uint) {
        if (_debt > 0) {
            return _coll.mul(NICR_PRECISION).div(_debt);
        }
        // Return the maximal value for uint256 if the Trove has a debt of 0. Represents "infinite" CR.
        else {
            // if (_debt == 0)
            return 2 ** 256 - 1;
        }
    }

    /**
     * HEDGEHOG UPDATES:
     * Change coll ration calculation from [coll] * [price] / [debt] to
     * [coll] / [debt] / [gasPrice]
     */
    function _computeCR(
        uint _coll,
        uint _debt,
        uint _price
    ) internal pure returns (uint) {
        if (_debt > 0) {
            uint newCollRatio = _coll.mul(DECIMAL_PRECISION).div(_debt).div(
                _price
            );

            return newCollRatio;
        }
        // Return the maximal value for uint256 if the Trove has a debt of 0. Represents "infinite" CR.
        else {
            // if (_debt == 0)
            return 2 ** 256 - 1;
        }
    }

    function _findPriceBelowMCR(
        uint256 _coll,
        uint256 _debt,
        uint256 _startPrice,
        uint _mcr
    ) internal pure returns (uint256) {
        uint256 step = 1; // Starting with the smallest step size
        uint256 GWEI = 10 ** 9;
        uint256 previousPrice = _startPrice * GWEI; // To track previous iteration's price

        for (
            uint256 price = _startPrice * GWEI;
            price <= 750 * GWEI;
            price += step
        ) {
            uint256 cr = _computeCR(_coll, _debt, price);

            if (cr < _mcr) {
                // Start decreasing the price to find the exact threshold
                for (
                    uint256 decreasedPrice = previousPrice;
                    decreasedPrice <= price &&
                        decreasedPrice >= _startPrice * GWEI; // Added protection against underflow
                    decreasedPrice -= GWEI
                ) {
                    if (_computeCR(_coll, _debt, decreasedPrice) < _mcr) {
                        return decreasedPrice;
                    }
                }
            }

            // Dynamically adjust step size
            if (cr < (_mcr - 200000000000000000)) {
                step = 20 * GWEI; // Increase step size if CR is much lower than MCR
            } else if (cr > (_mcr + 50000000000000000)) {
                step = GWEI; // Decrease step size if CR is much higher than MCR
            }

            previousPrice = price; // Update previous price for the next iteration
        }

        return 0; // return 0 if no price was found that brings the CR below MCR
    }
}

contracts/FeesRouter.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IBaseFeeLMAToken.sol";
import "./interfaces/IActivePool.sol";
import "./interfaces/IHOGStaking.sol";
import "hardhat/console.sol";

error InvalidIndex();
error InvalidAddress();
error InvalidLength();
error InvalidInput();
error TooManyConfigValues();

contract FeesRouter is AccessControl {
    bytes32 internal constant SETTER = keccak256("SETTER");
    bytes32 internal constant ULTIMATE_ADMIN = keccak256("ULTIMATE_ADMIN");
    bytes32 internal constant DEPLOYER = keccak256("DEPLOYER");

    struct FeeConfig {
        uint256 amountA;
        uint256 amountB;
        uint256 amountC;
        address addressA;
        address addressB;
        address addressC;
    }

    mapping(uint256 => FeeConfig) public debtFeeConfigs;
    mapping(uint256 => FeeConfig) public collFeeConfigs;

    uint256 public feeCount;
    IBaseFeeLMAToken baseFeeLMAToken;
    IActivePool activePool;
    IHOGStaking hogStaking;

    event DebtFeeConfigUpdated(
        address indexed setter,
        uint256 indexed percentage,
        uint256 amountA,
        uint256 amountB,
        uint256 amountC,
        address addressA,
        address addressB,
        address addressC
    );

    event CollFeeConfigUpdated(
        address indexed setter,
        uint256 indexed percentage,
        uint256 amountA,
        uint256 amountB,
        uint256 amountC,
        address addressA,
        address addressB,
        address addressC
    );

    constructor(address _defaultAdmin, address _ultimateAdmin) {
        if (address(_defaultAdmin) == address(0)) revert InvalidAddress();
        if (address(_ultimateAdmin) == address(0)) revert InvalidAddress();

        _grantRole(ULTIMATE_ADMIN, _ultimateAdmin);
        _setRoleAdmin(SETTER, ULTIMATE_ADMIN);
        _grantRole(SETTER, _defaultAdmin);
        _grantRole(DEPLOYER, msg.sender);
    }

    function setAddresses(
        IActivePool _activePool,
        IBaseFeeLMAToken _baseFeeLMAToken,
        IHOGStaking _hogStaking
    ) external onlyRole(DEPLOYER) {
        if (address(_activePool) == address(0)) revert InvalidAddress();
        if (address(_baseFeeLMAToken) == address(0)) revert InvalidAddress();
        if (address(_hogStaking) == address(0)) revert InvalidAddress();

        activePool = _activePool;
        baseFeeLMAToken = _baseFeeLMAToken;
        hogStaking = _hogStaking;

        _revokeRole(DEPLOYER, msg.sender);
    }

    function setFeeConfigs(
        uint256 _percentage,
        uint256 _amountA,
        uint256 _amountB,
        uint256 _amountC,
        address _addressA,
        address _addressB,
        address _addressC
    ) external onlyRole(SETTER) {
        if (_percentage % 5 != 0) revert InvalidIndex();
        if (_addressA == address(0)) revert InvalidAddress(); // At least A address should be initiated
        if (_amountA == 0) revert InvalidInput(); // At least A amount should be initiated

        debtFeeConfigs[_percentage] = FeeConfig(
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );

        collFeeConfigs[_percentage] = FeeConfig(
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );

        emit DebtFeeConfigUpdated(
            msg.sender,
            _percentage,
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );

        emit CollFeeConfigUpdated(
            msg.sender,
            _percentage,
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );
    }

    function setDebtFeeConfig(
        uint256 _percentage,
        uint256 _amountA,
        uint256 _amountB,
        uint256 _amountC,
        address _addressA,
        address _addressB,
        address _addressC
    ) external onlyRole(SETTER) {
        if (_percentage % 5 != 0) revert InvalidIndex();
        if (_addressA == address(0)) revert InvalidAddress(); // At least A address should be initiated
        if (_amountA == 0) revert InvalidInput(); // At least A amount should be initiated

        debtFeeConfigs[_percentage] = FeeConfig(
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );

        emit DebtFeeConfigUpdated(
            msg.sender,
            _percentage,
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );
    }

    function setCollFeeConfig(
        uint256 _percentage,
        uint256 _amountA,
        uint256 _amountB,
        uint256 _amountC,
        address _addressA,
        address _addressB,
        address _addressC
    ) external onlyRole(SETTER) {
        if (_percentage % 5 != 0) revert InvalidIndex();
        if (_addressA == address(0)) revert InvalidAddress(); // At least A address should be initiated
        if (_amountA == 0) revert InvalidInput(); // At least A amount should be initiated

        collFeeConfigs[_percentage] = FeeConfig(
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );

        emit CollFeeConfigUpdated(
            msg.sender,
            _percentage,
            _amountA,
            _amountB,
            _amountC,
            _addressA,
            _addressB,
            _addressC
        );
    }

    function distributeDebtFee(uint256 _debt, uint256 _fee) external {
        FeeConfig memory config = debtFeeConfigs[
            (((_fee * 100) / _debt) % 5) * 5
        ];
        uint256 amountA = _calculateAmount(_fee, config.amountA);
        uint256 amountB = _calculateAmount(_fee, config.amountB);
        uint256 amountC = _calculateAmount(_fee, config.amountC);

        uint256 totalAmounts = amountA + amountB + amountC;
        if (totalAmounts > _fee) {
            // Usually, that means that DAO treasure gets the extra dust
            amountA = amountA + totalAmounts - _fee;
        }

        IBaseFeeLMAToken _baseFeeLMAToken = baseFeeLMAToken;
        IHOGStaking _hogStaking = hogStaking;
        if (amountA > 0 && config.addressA != address(0)) {
            _baseFeeLMAToken.mint(config.addressA, amountA);

            _possiblyIncreaseHogStakingDebtBalance(
                config.addressA,
                amountA,
                _hogStaking
            );
        }
        if (amountB > 0 && config.addressB != address(0)) {
            _baseFeeLMAToken.mint(config.addressB, amountB);

            _possiblyIncreaseHogStakingDebtBalance(
                config.addressB,
                amountB,
                _hogStaking
            );
        }
        if (amountC > 0 && config.addressC != address(0)) {
            _baseFeeLMAToken.mint(config.addressB, amountC);

            _possiblyIncreaseHogStakingDebtBalance(
                config.addressC,
                amountC,
                _hogStaking
            );
        }
    }

    function distributeCollFee(uint256 _debt, uint256 _fee) external {
        FeeConfig memory config = collFeeConfigs[
            (((_fee * 100) / _debt) % 5) * 5
        ];
        uint256 amountA = _calculateAmount(_fee, config.amountA);
        uint256 amountB = _calculateAmount(_fee, config.amountB);
        uint256 amountC = _calculateAmount(_fee, config.amountC);

        uint256 totalAmounts = amountA + amountB + amountC;
        if (totalAmounts > _fee) {
            // Usually, that means that DAO treasure gets the extra dust
            amountA = amountA + totalAmounts - _fee;
        }

        IActivePool _activePool = activePool;
        IHOGStaking _hogStaking = hogStaking;

        if (amountA > 0 && config.addressA != address(0)) {
            _activePool.sendStETH(config.addressA, amountA);

            _possiblyIncreaseHogStakingCollBalance(
                config.addressA,
                amountA,
                _hogStaking
            );
        }
        if (amountB > 0 && config.addressB != address(0)) {
            _activePool.sendStETH(config.addressB, amountB);

            _possiblyIncreaseHogStakingCollBalance(
                config.addressA,
                amountA,
                _hogStaking
            );
        }
        if (amountC > 0 && config.addressC != address(0)) {
            _activePool.sendStETH(config.addressC, amountC);

            _possiblyIncreaseHogStakingCollBalance(
                config.addressA,
                amountA,
                _hogStaking
            );
        }
    }

    function _calculateAmount(
        uint256 _fee,
        uint256 _percentage
    ) internal pure returns (uint256) {
        return ((_fee * _percentage) / 100);
    }

    function _possiblyIncreaseHogStakingCollBalance(
        address _receiver,
        uint256 _amount,
        IHOGStaking _hogStaking
    ) internal {
        if (_receiver == address(_hogStaking)) {
            _hogStaking.increaseF_StETH(_amount);
        }
    }

    function _possiblyIncreaseHogStakingDebtBalance(
        address _receiver,
        uint256 _amount,
        IHOGStaking _hogStaking
    ) internal {
        if (_receiver == address(_hogStaking)) {
            hogStaking.increaseF_BaseFeeLMA(_amount);
        }
    }
}

