contracts/interfaces/IStabilityPool.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

/*
 * The Stability Pool holds BaseFeeLMA tokens deposited by Stability Pool depositors.
 *
 * When a trove is liquidated, then depending on system conditions, some of its BaseFeeLMA debt gets offset with
 * BaseFeeLMA in the Stability Pool:  that is, the offset debt evaporates, and an equal amount of BaseFeeLMA tokens in the Stability Pool is burned.
 *
 * Thus, a liquidation causes each depositor to receive a BaseFeeLMA loss, in proportion to their deposit as a share of total deposits.
 * They also receive an WStETH gain, as the WStETH collateral of the liquidated trove is distributed among Stability depositors,
 * in the same proportion.
 *
 * When a liquidation occurs, it depletes every deposit by the same fraction: for example, a liquidation that depletes 40%
 * of the total BaseFeeLMA in the Stability Pool, depletes 40% of each deposit.
 *
 * A deposit that has experienced a series of liquidations is termed a "compounded deposit": each liquidation depletes the deposit,
 * multiplying it by some factor in range ]0,1[
 *
 * Please see the implementation spec in the proof document, which closely follows on from the compounded deposit / WStETH gain derivations:
 * https://github.com/liquity/liquity/blob/master/papers/Scalable_Reward_Distribution_with_Compounding_Stakes.pdf
 *
 * --- HOG ISSUANCE TO STABILITY POOL DEPOSITORS ---
 *
 * A HOG issuance event occurs at every deposit operation, and every liquidation.
 *
 * Each deposit is tagged with the address of the front end through which it was made.
 *
 * All deposits earn a share of the issued HOG in proportion to the deposit as a share of total deposits. The HOG earned
 * by a given deposit, is split between the depositor and the front end through which the deposit was made, based on the front end's kickbackRate.
 *
 * Please see the system Readme for an overview:
 * https://github.com/liquity/dev/blob/main/README.md#hog-issuance-to-stability-providers
 */
interface IStabilityPool {
    // --- Events ---

    event StabilityPoolWStETHBalanceUpdated(uint _newBalance);
    event StabilityPoolBaseFeeLMABalanceUpdated(uint _newBalance);

    event BorrowerOperationsAddressChanged(
        address _newBorrowerOperationsAddress
    );
    event TroveManagerAddressChanged(address _newTroveManagerAddress);
    event ActivePoolAddressChanged(address _newActivePoolAddress);
    event DefaultPoolAddressChanged(address _newDefaultPoolAddress);
    event BaseFeeLMATokenAddressChanged(address _newBaseFeeLMATokenAddress);
    event SortedTrovesAddressChanged(address _newSortedTrovesAddress);
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event CommunityIssuanceAddressChanged(address _newCommunityIssuanceAddress);

    event P_Updated(uint _P);
    event S_Updated(uint _S, uint128 _epoch, uint128 _scale);
    event G_Updated(uint _G, uint128 _epoch, uint128 _scale);
    event EpochUpdated(uint128 _currentEpoch);
    event ScaleUpdated(uint128 _currentScale);

    event DepositSnapshotUpdated(
        address indexed _depositor,
        uint _P,
        uint _S,
        uint _G
    );

    event WStETHGainWithdrawn(
        address indexed _depositor,
        uint _WStETH,
        uint _BaseFeeLMALoss
    );
    event HOGPaidToDepositor(address indexed _depositor, uint _HOG);
    event WStETHSent(address _to, uint _amount);

    // --- Functions ---

    /*
     * Called only once on init, to set addresses of other Hedgehog contracts
     * Callable only by owner, renounces ownership at the end
     */
    function setAddresses(
        address _borrowerOperationsAddress,
        address _troveManagerAddress,
        address _activePoolAddress,
        address _baseFeeLMATokenAddress,
        address _sortedTrovesAddress,
        address _priceFeedAddress,
        address _communityIssuanceAddress
    ) external;

    /*
     * Initial checks:
     * - _amount is not zero
     * ---
     * - Triggers a HOG issuance, based on time passed since the last issuance. The HOG issuance is shared between *all* depositors and front ends
     * - Tags the deposit with the provided front end tag param, if it's a new deposit
     * - Sends depositor's accumulated gains (HOG, WStETH) to depositor
     * - Sends the tagged front end's accumulated HOG gains to the tagged front end
     * - Increases deposit and tagged front end's stake, and takes new snapshots for each.
     */
    function provideToSP(uint _amount) external;

    /*
     * Initial checks:
     * - _amount is zero or there are no under collateralized troves left in the system
     * - User has a non zero deposit
     * ---
     * - Triggers a HOG issuance, based on time passed since the last issuance. The HOG issuance is shared between *all* depositors and front ends
     * - Removes the deposit's front end tag if it is a full withdrawal
     * - Sends all depositor's accumulated gains (HOG, WStETH) to depositor
     * - Sends the tagged front end's accumulated HOG gains to the tagged front end
     * - Decreases deposit and tagged front end's stake, and takes new snapshots for each.
     *
     * If _amount > userDeposit, the user withdraws all of their compounded deposit.
     */
    function withdrawFromSP(uint _amount) external;

    /*
     * Initial checks:
     * - User has a non zero deposit
     * - User has an open trove
     * - User has some WStETH gain
     * ---
     * - Triggers a HOG issuance, based on time passed since the last issuance. The HOG issuance is shared between *all* depositors and front ends
     * - Sends all depositor's HOG gain to  depositor
     * - Sends all tagged front end's HOG gain to the tagged front end
     * - Transfers the depositor's entire WStETH gain from the Stability Pool to the caller's trove
     * - Leaves their compounded deposit in the Stability Pool
     * - Updates snapshots for deposit and tagged front end stake
     */
    function withdrawWStETHGainToTrove(
        address _upperHint,
        address _lowerHint
    ) external;

    /*
     * Initial checks:
     * - Caller is TroveManager
     * ---
     * Cancels out the specified debt against the BaseFeeLMA contained in the Stability Pool (as far as possible)
     * and transfers the Trove's WStETH collateral from ActivePool to StabilityPool.
     * Only called by liquidation functions in the TroveManager.
     */
    function offset(uint _debt, uint _coll) external;

    /*
     * Returns the total amount of WStETH held by the pool, accounted in an internal variable instead of `balance`,
     * to exclude edge cases like WStETH received from a self-destruct.
     */
    function getWStETH() external view returns (uint);

    /*
     * Returns BaseFeeLMA held in the pool. Changes when users deposit/withdraw, and when Trove debt is offset.
     */
    function getTotalBaseFeeLMADeposits() external view returns (uint);

    /*
     * Calculates the WStETH gain earned by the deposit since its last snapshots were taken.
     */
    function getDepositorWStETHGain(
        address _depositor
    ) external view returns (uint);

    /*
     * Calculate the HOG gain earned by a deposit since its last snapshots were taken.
     * If not tagged with a front end, the depositor gets a 100% cut of what their deposit earned.
     * Otherwise, their cut of the deposit's earnings is equal to the kickbackRate, set by the front end through
     * which they made their deposit.
     */
    function getDepositorHOGGain(
        address _depositor
    ) external view returns (uint);

    /*
     * Return the user's compounded deposit.
     */
    function getCompoundedBaseFeeLMADeposit(
        address _depositor
    ) external view returns (uint);
}

contracts/interfaces/ITroveManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "./IHedgehogBase.sol";
import "./IStabilityPool.sol";
import "./IBaseFeeLMAToken.sol";
import "./IHOGToken.sol";

// Common interface for the Trove Manager.
interface ITroveManager is IHedgehogBase {
    // --- Events ---

    event BorrowerOperationsAddressChanged(
        address _newBorrowerOperationsAddress
    );
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event BaseFeeLMATokenAddressChanged(address _newBaseFeeLMATokenAddress);
    event ActivePoolAddressChanged(address _activePoolAddress);
    event DefaultPoolAddressChanged(address _defaultPoolAddress);
    event StabilityPoolAddressChanged(address _stabilityPoolAddress);
    event GasPoolAddressChanged(address _gasPoolAddress);
    event CollSurplusPoolAddressChanged(address _collSurplusPoolAddress);
    event SortedTrovesAddressChanged(address _sortedTrovesAddress);
    event HOGTokenAddressChanged(address _hogTokenAddress);

    event Liquidation(
        uint _liquidatedDebt,
        uint _liquidatedColl,
        uint _collGasCompensation,
        uint _BaseFeeLMAGasCompensation
    );
    event Redemption(
        uint _attemptedBaseFeeLMAAmount,
        uint _actualBaseFeeLMAAmount,
        uint _WStETHSent,
        uint _WStETHFee
    );
    event TroveUpdated(
        address indexed _borrower,
        uint _debt,
        uint _coll,
        uint stake,
        uint8 operation
    );
    event TroveLiquidated(
        address indexed _borrower,
        uint _debt,
        uint _coll,
        uint8 operation
    );
    event BaseRateUpdated(uint _baseRate);
    event LastFeeOpTimeUpdated(uint _lastFeeOpTime);
    event TotalStakesUpdated(uint _newTotalStakes);
    event SystemSnapshotsUpdated(
        uint _totalStakesSnapshot,
        uint _totalCollateralSnapshot
    );
    event LTermsUpdated(uint _L_WStETH, uint _L_BaseFeeLMADebt);
    event TroveSnapshotsUpdated(uint _L_WStETH, uint _L_BaseFeeLMADebt);
    event TroveIndexUpdated(address _borrower, uint _newIndex);

    // --- Functions ---

    function setAddresses(
        address _borrowerOperationsAddress,
        address _activePoolAddress,
        address _defaultPoolAddress,
        address _stabilityPoolAddress,
        address _gasPoolAddress,
        address _collSurplusPoolAddress,
        address _priceFeedAddress,
        address _baseFeeLMATokenAddress,
        address _sortedTrovesAddress,
        address _hogTokenAddress
    ) external;

    function stabilityPool() external view returns (IStabilityPool);

    function baseFeeLMAToken() external view returns (IBaseFeeLMAToken);

    function hogToken() external view returns (IHOGToken);

    function getTroveOwnersCount() external view returns (uint);

    function getTroveFromTroveOwnersArray(
        uint _index
    ) external view returns (address);

    function getNominalICR(address _borrower) external view returns (uint);

    function getCurrentICR(
        address _borrower,
        uint _price
    ) external view returns (uint);

    function liquidate(address _borrower) external;

    function liquidateTroves(uint _n) external;

    function batchLiquidateTroves(address[] calldata _troveArray) external;

    function redeemCollateral(
        uint _BaseFeeLMAAmount,
        address _firstRedemptionHint,
        address _upperPartialRedemptionHint,
        address _lowerPartialRedemptionHint,
        uint _partialRedemptionHintNICR,
        uint _maxIterations,
        uint _maxFee
    ) external;

    function updateStakeAndTotalStakes(
        address _borrower
    ) external returns (uint);

    function updateTroveRewardSnapshots(address _borrower) external;

    function addTroveOwnerToArray(
        address _borrower
    ) external returns (uint index);

    function applyPendingRewards(address _borrower) external;

    function getPendingWStETHReward(
        address _borrower
    ) external view returns (uint);

    function getPendingBaseFeeLMADebtReward(
        address _borrower
    ) external view returns (uint);

    function hasPendingRewards(address _borrower) external view returns (bool);

    function getEntireDebtAndColl(
        address _borrower
    )
        external
        view
        returns (
            uint debt,
            uint coll,
            uint pendingBaseFeeLMADebtReward,
            uint pendingWStETHReward
        );

    function closeTrove(address _borrower) external;

    function removeStake(address _borrower) external;

    function getRedemptionRate(
        uint _redemptionColl
    ) external view returns (uint);

    function getRedemptionRateWithDecay(
        uint _redemptionColl
    ) external view returns (uint);

    function getRedemptionFeeWithDecay(
        uint _WStETHDrawn
    ) external view returns (uint);

    function getBorrowingRate(
        uint _issuedBaseFeeLMA
    ) external view returns (uint);

    function getBorrowingRateWithDecay(
        uint _issuedBaseFeeLMA
    ) external view returns (uint);

    function getBorrowingFee(
        uint BaseFeeLMADebt
    ) external view returns (uint, uint);

    function getBorrowingFeeWithDecay(
        uint _BaseFeeLMADebt
    ) external view returns (uint);

    function updateBaseRateFromBorrowing(uint _baseRate) external;

    function decayBaseRateFromBorrowing() external;

    function getTroveStatus(address _borrower) external view returns (uint);

    function getTroveStake(address _borrower) external view returns (uint);

    function getTroveDebt(address _borrower) external view returns (uint);

    function getTroveColl(address _borrower) external view returns (uint);

    function setTroveStatus(address _borrower, uint num) external;

    function increaseTroveColl(
        address _borrower,
        uint _collIncrease
    ) external returns (uint);

    function decreaseTroveColl(
        address _borrower,
        uint _collDecrease
    ) external returns (uint);

    function increaseTroveDebt(
        address _borrower,
        uint _debtIncrease
    ) external returns (uint);

    function decreaseTroveDebt(
        address _borrower,
        uint _collDecrease
    ) external returns (uint);

    function getTCR(uint _price) external view returns (uint);

    function checkRecoveryMode(uint _price) external view returns (bool);
}

contracts/StabilityPool.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "./interfaces/IBorrowerOperations.sol";
import "./interfaces/IStabilityPool.sol";
import "./interfaces/IBorrowerOperations.sol";
import "./interfaces/ITroveManager.sol";
import "./interfaces/IBaseFeeLMAToken.sol";
import "./interfaces/ISortedTroves.sol";
import "./interfaces/ICommunityIssuance.sol";
import "./dependencies/HedgehogBase.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./dependencies/LiquitySafeMath128.sol";
import "./dependencies/CheckContract.sol";

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @notice Fork of Liquity's Stability Pool. Logic remains unchanged.
 * Changes to the contract:
 * - Raised pragma version
 * - Removed an import of IStabilityPool Interface
 * Even though SafeMath is no longer required, the decision was made to keep it to avoid human factor errors
 *
 * The Stability Pool holds BaseFeeLMA tokens deposited by Stability Pool depositors.
 *
 * When a trove is liquidated, then depending on system conditions, some of its BaseFeeLMA debt gets offset with
 * BaseFeeLMA in the Stability Pool:  that is, the offset debt evaporates, and an equal amount of BaseFeeLMA tokens in the Stability Pool is burned.
 *
 * Thus, a liquidation causes each depositor to receive a BaseFeeLMA loss, in proportion to their deposit as a share of total deposits.
 * They also receive an WStETH gain, as the WStETH collateral of the liquidated trove is distributed among Stability depositors,
 * in the same proportion.
 *
 * When a liquidation occurs, it depletes every deposit by the same fraction: for example, a liquidation that depletes 40%
 * of the total BaseFeeLMA in the Stability Pool, depletes 40% of each deposit.
 *
 * A deposit that has experienced a series of liquidations is termed a "compounded deposit": each liquidation depletes the deposit,
 * multiplying it by some factor in range ]0,1[
 *
 *
 * --- IMPLEMENTATION ---
 *
 * We use a highly scalable method of tracking deposits and WStETH gains that has O(1) complexity.
 *
 * When a liquidation occurs, rather than updating each depositor's deposit and WStETH gain, we simply update two state variables:
 * a product P, and a sum S.
 *
 * A mathematical manipulation allows us to factor out the initial deposit, and accurately track all depositors' compounded deposits
 * and accumulated WStETH gains over time, as liquidations occur, using just these two variables P and S. When depositors join the
 * Stability Pool, they get a snapshot of the latest P and S: P_t and S_t, respectively.
 *
 * The formula for a depositor's accumulated WStETH gain is derived here:
 * https://github.com/liquity/dev/blob/main/papers/Scalable_Reward_Distribution_with_Compounding_Stakes.pdf
 *
 * For a given deposit d_t, the ratio P/P_t tells us the factor by which a deposit has decreased since it joined the Stability Pool,
 * and the term d_t * (S - S_t)/P_t gives us the deposit's total accumulated WStETH gain.
 *
 * Each liquidation updates the product P and sum S. After a series of liquidations, a compounded deposit and corresponding WStETH gain
 * can be calculated using the initial deposit, the depositors snapshots of P and S, and the latest values of P and S.
 *
 * Any time a depositor updates their deposit (withdrawal, top-up) their accumulated WStETH gain is paid out, their new deposit is recorded
 * (based on their latest compounded deposit and modified by the withdrawal/top-up), and they receive new snapshots of the latest P and S.
 * Essentially, they make a fresh deposit that overwrites the old one.
 *
 *
 * --- SCALE FACTOR ---
 *
 * Since P is a running product in range ]0,1] that is always-decreasing, it should never reach 0 when multiplied by a number in range ]0,1[.
 * Unfortunately, Solidity floor division always reaches 0, sooner or later.
 *
 * A series of liquidations that nearly empty the Pool (and thus each multiply P by a very small number in range ]0,1[ ) may push P
 * to its 18 digit decimal limit, and round it to 0, when in fact the Pool hasn't been emptied: this would break deposit tracking.
 *
 * So, to track P accurately, we use a scale factor: if a liquidation would cause P to decrease to <1e-9 (and be rounded to 0 by Solidity),
 * we first multiply P by 1e9, and increment a currentScale factor by 1.
 *
 * The added benefit of using 1e9 for the scale factor (rather than 1e18) is that it ensures negligible precision loss close to the
 * scale boundary: when P is at its minimum value of 1e9, the relative precision loss in P due to floor division is only on the
 * order of 1e-9.
 *
 * --- EPOCHS ---
 *
 * Whenever a liquidation fully empties the Stability Pool, all deposits should become 0. However, setting P to 0 would make P be 0
 * forever, and break all future reward calculations.
 *
 * So, every time the Stability Pool is emptied by a liquidation, we reset P = 1 and currentScale = 0, and increment the currentEpoch by 1.
 *
 * --- TRACKING DEPOSIT OVER SCALE CHANGES AND EPOCHS ---
 *
 * When a deposit is made, it gets snapshots of the currentEpoch and the currentScale.
 *
 * When calculating a compounded deposit, we compare the current epoch to the deposit's epoch snapshot. If the current epoch is newer,
 * then the deposit was present during a pool-emptying liquidation, and necessarily has been depleted to 0.
 *
 * Otherwise, we then compare the current scale to the deposit's scale snapshot. If they're equal, the compounded deposit is given by d_t * P/P_t.
 * If it spans one scale change, it is given by d_t * P/(P_t * 1e9). If it spans more than one scale change, we define the compounded deposit
 * as 0, since it is now less than 1e-9'th of its initial value (e.g. a deposit of 1 billion BaseFeeLMA has depleted to < 1 BaseFeeLMA).
 *
 *
 *  --- TRACKING DEPOSITOR'S WStETH GAIN OVER SCALE CHANGES AND EPOCHS ---
 *
 * In the current epoch, the latest value of S is stored upon each scale change, and the mapping (scale -> S) is stored for each epoch.
 *
 * This allows us to calculate a deposit's accumulated WStETH gain, during the epoch in which the deposit was non-zero and earned WStETH.
 *
 * We calculate the depositor's accumulated WStETH gain for the scale at which they made the deposit, using the WStETH gain formula:
 * e_1 = d_t * (S - S_t) / P_t
 *
 * and also for scale after, taking care to divide the latter by a factor of 1e9:
 * e_2 = d_t * S / (P_t * 1e9)
 *
 * The gain in the second scale will be full, as the starting point was in the previous scale, thus no need to subtract anything.
 * The deposit therefore was present for reward events from the beginning of that second scale.
 *
 *        S_i-S_t + S_{i+1}
 *      .<--------.------------>
 *      .         .
 *      . S_i     .   S_{i+1}
 *   <--.-------->.<----------->
 *   S_t.         .
 *   <->.         .
 *      t         .
 *  |---+---------|-------------|-----...
 *         i            i+1
 *
 * The sum of (e_1 + e_2) captures the depositor's total accumulated WStETH gain, handling the case where their
 * deposit spanned one scale change. We only care about gains across one scale change, since the compounded
 * deposit is defined as being 0 once it has spanned more than one scale change.
 *
 *
 * --- UPDATING P WHEN A LIQUIDATION OCCURS ---
 *
 * Please see the implementation spec in the proof document, which closely follows on from the compounded deposit / WStETH gain derivations:
 * https://github.com/liquity/liquity/blob/master/papers/Scalable_Reward_Distribution_with_Compounding_Stakes.pdf
 *
 *
 * --- HOG ISSUANCE TO STABILITY POOL DEPOSITORS ---
 *
 * A HOG issuance event occurs at every deposit operation, and every liquidation.
 *
 * Each deposit is tagged with the address of the front end through which it was made.
 *
 * All deposits earn a share of the issued HOG in proportion to the deposit as a share of total deposits. The HOG earned
 * by a given deposit, is split between the depositor and the front end through which the deposit was made, based on the front end's kickbackRate.
 *
 * Please see the system Readme for an overview:
 * https://github.com/liquity/dev/blob/main/README.md#hog-issuance-to-stability-providers
 *
 * We use the same mathematical product-sum approach to track HOG gains for depositors, where 'G' is the sum corresponding to HOG gains.
 * The product P (and snapshot P_t) is re-used, as the ratio P/P_t tracks a deposit's depletion due to liquidations.
 *
 */
contract StabilityPool is HedgehogBase, Ownable, CheckContract {
    using LiquitySafeMath128 for uint128;
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    string public constant NAME = "StabilityPool";

    IBorrowerOperations public borrowerOperations;

    ITroveManager public troveManager;

    IBaseFeeLMAToken public baseFeeLMAToken;

    // Needed to check if there are pending liquidations
    ISortedTroves public sortedTroves;

    ICommunityIssuance public communityIssuance;

    uint256 internal WStETH; // deposited wStETH tracker
    IERC20 public WStETHToken;

    // Tracker for BaseFeeLMA held in the pool. Changes when users deposit/withdraw, and when Trove debt is offset.
    uint256 internal totalBaseFeeLMADeposits;

    // --- Data structures ---

    struct Deposit {
        uint initialValue;
    }

    struct Snapshots {
        uint S;
        uint P;
        uint G;
        uint128 scale;
        uint128 epoch;
    }

    mapping(address => Deposit) public deposits; // depositor address -> Deposit struct
    mapping(address => Snapshots) public depositSnapshots; // depositor address -> snapshots struct

    /*  Product 'P': Running product by which to multiply an initial deposit, in order to find the current compounded deposit,
     * after a series of liquidations have occurred, each of which cancel some BaseFeeLMA debt with the deposit.
     *
     * During its lifetime, a deposit's value evolves from d_t to d_t * P / P_t , where P_t
     * is the snapshot of P taken at the instant the deposit was made. 18-digit decimal.
     */
    uint public P = DECIMAL_PRECISION;

    uint public constant SCALE_FACTOR = 1e9;

    // Each time the scale of P shifts by SCALE_FACTOR, the scale is incremented by 1
    uint128 public currentScale;

    // With each offset that fully empties the Pool, the epoch is incremented by 1
    uint128 public currentEpoch;

    /* WStETH Gain sum 'S': During its lifetime, each deposit d_t earns an WStETH gain of ( d_t * [S - S_t] )/P_t, where S_t
     * is the depositor's snapshot of S taken at the time t when the deposit was made.
     *
     * The 'S' sums are stored in a nested mapping (epoch => scale => sum):
     *
     * - The inner mapping records the sum S at different scales
     * - The outer mapping records the (scale => sum) mappings, for different epochs.
     */
    mapping(uint128 => mapping(uint128 => uint)) public epochToScaleToSum;

    /*
     * Similarly, the sum 'G' is used to calculate HOG gains. During it's lifetime, each deposit d_t earns a HOG gain of
     *  ( d_t * [G - G_t] )/P_t, where G_t is the depositor's snapshot of G taken at time t when  the deposit was made.
     *
     *  HOG reward events occur are triggered by depositor operations (new deposit, topup, withdrawal), and liquidations.
     *  In each case, the HOG reward is issued (i.e. G is updated), before other state changes are made.
     */
    mapping(uint128 => mapping(uint128 => uint)) public epochToScaleToG;

    // Error tracker for the error correction in the HOG issuance calculation
    uint public lastHOGError;
    // Error trackers for the error correction in the offset calculation
    uint public lastWStETHError_Offset;
    uint public lastBaseFeeLMALossError_Offset;

    // --- Events ---

    event StabilityPoolWStETHBalanceUpdated(uint _newBalance);
    event StabilityPoolBaseFeeLMABalanceUpdated(uint _newBalance);

    event BorrowerOperationsAddressChanged(
        address _newBorrowerOperationsAddress
    );
    event TroveManagerAddressChanged(address _newTroveManagerAddress);
    event ActivePoolAddressChanged(address _newActivePoolAddress);
    event DefaultPoolAddressChanged(address _newDefaultPoolAddress);
    event BaseFeeLMATokenAddressChanged(address _newBaseFeeLMATokenAddress);
    event SortedTrovesAddressChanged(address _newSortedTrovesAddress);
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event CommunityIssuanceAddressChanged(address _newCommunityIssuanceAddress);
    event WStETHTokenAddressUpdated(IERC20 _WStEthAddress);

    event P_Updated(uint _P);
    event S_Updated(uint _S, uint128 _epoch, uint128 _scale);
    event G_Updated(uint _G, uint128 _epoch, uint128 _scale);
    event EpochUpdated(uint128 _currentEpoch);
    event ScaleUpdated(uint128 _currentScale);

    event DepositSnapshotUpdated(
        address indexed _depositor,
        uint _P,
        uint _S,
        uint _G
    );

    event UserDepositChanged(address indexed _depositor, uint _newDeposit);

    event WStETHGainWithdrawn(
        address indexed _depositor,
        uint _WStETH,
        uint _BaseFeeLMALoss
    );
    event HOGPaidToDepositor(address indexed _depositor, uint _HOG);
    event WStETHSent(address _to, uint _amount);

    constructor(
        uint _gasComp,
        uint _minNetDebt,
        uint _CCR
    ) HedgehogBase(_gasComp, _minNetDebt, _CCR) {}

    // --- Contract setters ---

    /**
     * HEDGEHOG UPDATES:
     * ERC20 is used as a collateral instead of native token.
     * Setting erc20 address in the initialisation
     */
    function setAddresses(
        address _borrowerOperationsAddress,
        address _troveManagerAddress,
        address _activePoolAddress,
        address _baseFeeLMATokenAddress,
        address _sortedTrovesAddress,
        address _priceFeedAddress,
        address _communityIssuanceAddress,
        IERC20 _WStETHTokenAddress
    ) external onlyOwner {
        checkContract(_borrowerOperationsAddress);
        checkContract(_troveManagerAddress);
        checkContract(_activePoolAddress);
        checkContract(_baseFeeLMATokenAddress);
        checkContract(_sortedTrovesAddress);
        checkContract(_priceFeedAddress);
        checkContract(_communityIssuanceAddress);
        checkContract(address(_WStETHTokenAddress));

        borrowerOperations = IBorrowerOperations(_borrowerOperationsAddress);
        troveManager = ITroveManager(_troveManagerAddress);
        activePool = IActivePool(_activePoolAddress);
        baseFeeLMAToken = IBaseFeeLMAToken(_baseFeeLMATokenAddress);
        sortedTroves = ISortedTroves(_sortedTrovesAddress);
        priceFeed = IPriceFeed(_priceFeedAddress);
        communityIssuance = ICommunityIssuance(_communityIssuanceAddress);
        WStETHToken = _WStETHTokenAddress;

        emit BorrowerOperationsAddressChanged(_borrowerOperationsAddress);
        emit TroveManagerAddressChanged(_troveManagerAddress);
        emit ActivePoolAddressChanged(_activePoolAddress);
        emit BaseFeeLMATokenAddressChanged(_baseFeeLMATokenAddress);
        emit SortedTrovesAddressChanged(_sortedTrovesAddress);
        emit PriceFeedAddressChanged(_priceFeedAddress);
        emit CommunityIssuanceAddressChanged(_communityIssuanceAddress);
        emit WStETHTokenAddressUpdated(_WStETHTokenAddress);

        renounceOwnership();
    }

    // --- Getters for public variables. Required by IPool interface ---

    function getWStETH() external view returns (uint) {
        return WStETH;
    }

    function getTotalBaseFeeLMADeposits() external view returns (uint) {
        return totalBaseFeeLMADeposits;
    }

    // --- External Depositor Functions ---

    /*  provideToSP():
     *
     * - Triggers a HOG issuance, based on time passed since the last issuance. The HOG issuance is shared between *all* depositors and front ends
     * - Tags the deposit with the provided front end tag param, if it's a new deposit
     * - Sends depositor's accumulated gains (HOG, WStETH) to depositor
     * - Sends the tagged front end's accumulated HOG gains to the tagged front end
     * - Increases deposit and tagged front end's stake, and takes new snapshots for each.
     */
    function provideToSP(uint _amount) external {
        _requireNonZeroAmount(_amount);

        uint initialDeposit = deposits[msg.sender].initialValue;

        ICommunityIssuance communityIssuanceCached = communityIssuance;

        _triggerHOGIssuance(communityIssuanceCached);

        uint depositorWStETHGain = getDepositorWStETHGain(msg.sender);
        uint compoundedBaseFeeLMADeposit = getCompoundedBaseFeeLMADeposit(
            msg.sender
        );
        uint BaseFeeLMALoss = initialDeposit.sub(compoundedBaseFeeLMADeposit); // Needed only for event log

        // HEDGEHOG UPDATES: No longer perfor any kind of "frontend" payments
        // First pay out any HOG gains
        _payOutHOGGains(communityIssuanceCached, msg.sender);

        _sendBaseFeeLMAtoStabilityPool(msg.sender, _amount);

        uint newDeposit = compoundedBaseFeeLMADeposit.add(_amount);
        _updateDepositAndSnapshots(msg.sender, newDeposit);
        emit UserDepositChanged(msg.sender, newDeposit);

        emit WStETHGainWithdrawn(
            msg.sender,
            depositorWStETHGain,
            BaseFeeLMALoss
        ); // BaseFeeLMA Loss required for event log

        _sendWStETHGainToDepositor(depositorWStETHGain);
    }

    /*  withdrawFromSP():
     *
     * - Triggers a HOG issuance, based on time passed since the last issuance. The HOG issuance is shared between *all* depositors and front ends
     * - Removes the deposit's front end tag if it is a full withdrawal
     * - Sends all depositor's accumulated gains (HOG, WStETH) to depositor
     * - Sends the tagged front end's accumulated HOG gains to the tagged front end
     * - Decreases deposit and tagged front end's stake, and takes new snapshots for each.
     *
     * If _amount > userDeposit, the user withdraws all of their compounded deposit.
     */
    function withdrawFromSP(uint _amount) external {
        if (_amount != 0) {
            _requireNoUnderCollateralizedTroves();
        }
        uint initialDeposit = deposits[msg.sender].initialValue;
        _requireUserHasDeposit(initialDeposit);

        ICommunityIssuance communityIssuanceCached = communityIssuance;

        _triggerHOGIssuance(communityIssuanceCached);

        uint depositorWStETHGain = getDepositorWStETHGain(msg.sender);

        uint compoundedBaseFeeLMADeposit = getCompoundedBaseFeeLMADeposit(
            msg.sender
        );
        uint BaseFeeLMAtoWithdraw = LiquityMath._min(
            _amount,
            compoundedBaseFeeLMADeposit
        );
        uint BaseFeeLMALoss = initialDeposit.sub(compoundedBaseFeeLMADeposit); // Needed only for event log

        // HEDGEHOG UPDATES: No longer perfor any kind of "frontend" payments
        // First pay out any HOG gains
        _payOutHOGGains(communityIssuanceCached, msg.sender);

        _sendBaseFeeLMAToDepositor(msg.sender, BaseFeeLMAtoWithdraw);

        // Update deposit
        uint newDeposit = compoundedBaseFeeLMADeposit.sub(BaseFeeLMAtoWithdraw);
        _updateDepositAndSnapshots(msg.sender, newDeposit);
        emit UserDepositChanged(msg.sender, newDeposit);

        emit WStETHGainWithdrawn(
            msg.sender,
            depositorWStETHGain,
            BaseFeeLMALoss
        ); // BaseFeeLMA Loss required for event log

        _sendWStETHGainToDepositor(depositorWStETHGain);
    }

    /* withdrawWStETHGainToTrove:

     * HEDGEHOG UPDATES: use SafeERC20 safe transfer instead of native token transfer. Therefore 
     * transfer value isn't paste anymore as a {value: }, but as a separate input param

     * - Triggers a HOG issuance, based on time passed since the last issuance. The HOG issuance is shared between *all* depositors and front ends
     * - Sends all depositor's HOG gain to  depositor
     * - Sends all tagged front end's HOG gain to the tagged front end
     * - Transfers the depositor's entire WStETH gain from the Stability Pool to the caller's trove
     * - Leaves their compounded deposit in the Stability Pool
     * - Updates snapshots for deposit and tagged front end stake */
    function withdrawWStETHGainToTrove(
        address _upperHint,
        address _lowerHint
    ) external {
        uint initialDeposit = deposits[msg.sender].initialValue;
        _requireUserHasDeposit(initialDeposit);
        _requireUserHasTrove(msg.sender);
        _requireUserHasWStETHGain(msg.sender);

        ICommunityIssuance communityIssuanceCached = communityIssuance;

        _triggerHOGIssuance(communityIssuanceCached);

        uint depositorWStETHGain = getDepositorWStETHGain(msg.sender);

        uint compoundedBaseFeeLMADeposit = getCompoundedBaseFeeLMADeposit(
            msg.sender
        );
        uint BaseFeeLMALoss = initialDeposit.sub(compoundedBaseFeeLMADeposit); // Needed only for event log

        // HEDGEHOG UPDATES: No longer perfor any kind of "frontend" payments
        // First pay out any HOG gains
        _payOutHOGGains(communityIssuanceCached, msg.sender);

        _updateDepositAndSnapshots(msg.sender, compoundedBaseFeeLMADeposit);

        /* Emit events before transferring WStETH gain to Trove.
         This lets the event log make more sense (i.e. so it appears that first the WStETH gain is withdrawn
        and then it is deposited into the Trove, not the other way around). */
        emit WStETHGainWithdrawn(
            msg.sender,
            depositorWStETHGain,
            BaseFeeLMALoss
        );
        emit UserDepositChanged(msg.sender, compoundedBaseFeeLMADeposit);

        WStETH = WStETH.sub(depositorWStETHGain);
        emit StabilityPoolWStETHBalanceUpdated(WStETH);
        emit WStETHSent(msg.sender, depositorWStETHGain);

        borrowerOperations.moveWStETHGainToTrove(
            msg.sender,
            _upperHint,
            _lowerHint,
            depositorWStETHGain
        );
    }

    // --- HOG issuance functions ---

    function _triggerHOGIssuance(
        ICommunityIssuance _communityIssuance
    ) internal {
        uint HOGIssuance = _communityIssuance.issueHOG();
        _updateG(HOGIssuance);
    }

    function _updateG(uint _HOGIssuance) internal {
        uint totalBaseFeeLMA = totalBaseFeeLMADeposits; // cached to save an SLOAD
        /*
         * When total deposits is 0, G is not updated. In this case, the HOG issued can not be obtained by later
         * depositors - it is missed out on, and remains in the balanceof the CommunityIssuance contract.
         *
         */
        if (totalBaseFeeLMA == 0 || _HOGIssuance == 0) {
            return;
        }

        uint HOGPerUnitStaked;
        HOGPerUnitStaked = _computeHOGPerUnitStaked(
            _HOGIssuance,
            totalBaseFeeLMA
        );

        uint marginalHOGGain = HOGPerUnitStaked.mul(P);
        epochToScaleToG[currentEpoch][currentScale] = epochToScaleToG[
            currentEpoch
        ][currentScale].add(marginalHOGGain);

        emit G_Updated(
            epochToScaleToG[currentEpoch][currentScale],
            currentEpoch,
            currentScale
        );
    }

    function _computeHOGPerUnitStaked(
        uint _HOGIssuance,
        uint _totalBaseFeeLMADeposits
    ) internal returns (uint) {
        /*
         * Calculate the HOG-per-unit staked.  Division uses a "feedback" error correction, to keep the
         * cumulative error low in the running total G:
         *
         * 1) Form a numerator which compensates for the floor division error that occurred the last time this
         * function was called.
         * 2) Calculate "per-unit-staked" ratio.
         * 3) Multiply the ratio back by its denominator, to reveal the current floor division error.
         * 4) Store this error for use in the next correction when this function is called.
         * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
         */
        uint HOGNumerator = _HOGIssuance.mul(DECIMAL_PRECISION).add(
            lastHOGError
        );

        uint HOGPerUnitStaked = HOGNumerator.div(_totalBaseFeeLMADeposits);
        lastHOGError = HOGNumerator.sub(
            HOGPerUnitStaked.mul(_totalBaseFeeLMADeposits)
        );

        return HOGPerUnitStaked;
    }

    // --- Liquidation functions ---

    /*
     * Cancels out the specified debt against the BaseFeeLMA contained in the Stability Pool (as far as possible)
     * and transfers the Trove's WStETH collateral from ActivePool to StabilityPool.
     * Only called by liquidation functions in the TroveManager.
     */
    function offset(uint _debtToOffset, uint _collToAdd) external {
        _requireCallerIsTroveManager();
        uint totalBaseFeeLMA = totalBaseFeeLMADeposits; // cached to save an SLOAD
        if (totalBaseFeeLMA == 0 || _debtToOffset == 0) {
            return;
        }

        _triggerHOGIssuance(communityIssuance);

        (
            uint WStETHGainPerUnitStaked,
            uint BaseFeeLMALossPerUnitStaked
        ) = _computeRewardsPerUnitStaked(
                _collToAdd,
                _debtToOffset,
                totalBaseFeeLMA
            );

        _updateRewardSumAndProduct(
            WStETHGainPerUnitStaked,
            BaseFeeLMALossPerUnitStaked
        ); // updates S and P

        _moveOffsetCollAndDebt(_collToAdd, _debtToOffset);
    }

    // --- Offset helper functions ---

    function _computeRewardsPerUnitStaked(
        uint _collToAdd,
        uint _debtToOffset,
        uint _totalBaseFeeLMADeposits
    )
        internal
        returns (uint WStETHGainPerUnitStaked, uint BaseFeeLMALossPerUnitStaked)
    {
        /*
         * Compute the BaseFeeLMA and WStETH rewards. Uses a "feedback" error correction, to keep
         * the cumulative error in the P and S state variables low:
         *
         * 1) Form numerators which compensate for the floor division errors that occurred the last time this
         * function was called.
         * 2) Calculate "per-unit-staked" ratios.
         * 3) Multiply each ratio back by its denominator, to reveal the current floor division error.
         * 4) Store these errors for use in the next correction when this function is called.
         * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
         */
        uint WStETHNumerator = _collToAdd.mul(DECIMAL_PRECISION).add(
            lastWStETHError_Offset
        );

        assert(_debtToOffset <= _totalBaseFeeLMADeposits);
        if (_debtToOffset == _totalBaseFeeLMADeposits) {
            BaseFeeLMALossPerUnitStaked = DECIMAL_PRECISION; // When the Pool depletes to 0, so does each deposit
            lastBaseFeeLMALossError_Offset = 0;
        } else {
            uint BaseFeeLMALossNumerator = _debtToOffset
                .mul(DECIMAL_PRECISION)
                .sub(lastBaseFeeLMALossError_Offset);
            /*
             * Add 1 to make error in quotient positive. We want "slightly too much" BaseFeeLMA loss,
             * which ensures the error in any given compoundedBaseFeeLMADeposit favors the Stability Pool.
             */
            BaseFeeLMALossPerUnitStaked = (
                BaseFeeLMALossNumerator.div(_totalBaseFeeLMADeposits)
            ).add(1);
            lastBaseFeeLMALossError_Offset = (
                BaseFeeLMALossPerUnitStaked.mul(_totalBaseFeeLMADeposits)
            ).sub(BaseFeeLMALossNumerator);
        }

        WStETHGainPerUnitStaked = WStETHNumerator.div(_totalBaseFeeLMADeposits);
        lastWStETHError_Offset = WStETHNumerator.sub(
            WStETHGainPerUnitStaked.mul(_totalBaseFeeLMADeposits)
        );

        return (WStETHGainPerUnitStaked, BaseFeeLMALossPerUnitStaked);
    }

    // Update the Stability Pool reward sum S and product P
    function _updateRewardSumAndProduct(
        uint _WStETHGainPerUnitStaked,
        uint _BaseFeeLMALossPerUnitStaked
    ) internal {
        uint currentP = P;
        uint newP;

        assert(_BaseFeeLMALossPerUnitStaked <= DECIMAL_PRECISION);
        /*
         * The newProductFactor is the factor by which to change all deposits, due to the depletion of Stability Pool BaseFeeLMA in the liquidation.
         * We make the product factor 0 if there was a pool-emptying. Otherwise, it is (1 - BaseFeeLMALossPerUnitStaked)
         */
        uint newProductFactor = uint(DECIMAL_PRECISION).sub(
            _BaseFeeLMALossPerUnitStaked
        );

        uint128 currentScaleCached = currentScale;
        uint128 currentEpochCached = currentEpoch;
        uint currentS = epochToScaleToSum[currentEpochCached][
            currentScaleCached
        ];

        /*
         * Calculate the new S first, before we update P.
         * The WStETH gain for any given depositor from a liquidation depends on the value of their deposit
         * (and the value of totalDeposits) prior to the Stability being depleted by the debt in the liquidation.
         *
         * Since S corresponds to WStETH gain, and P to deposit loss, we update S first.
         */
        uint marginalWStETHGain = _WStETHGainPerUnitStaked.mul(currentP);
        uint newS = currentS.add(marginalWStETHGain);
        epochToScaleToSum[currentEpochCached][currentScaleCached] = newS;
        emit S_Updated(newS, currentEpochCached, currentScaleCached);

        // If the Stability Pool was emptied, increment the epoch, and reset the scale and product P
        if (newProductFactor == 0) {
            currentEpoch = currentEpochCached.add(1);
            emit EpochUpdated(currentEpoch);
            currentScale = 0;
            emit ScaleUpdated(currentScale);
            newP = DECIMAL_PRECISION;

            // If multiplying P by a non-zero product factor would reduce P below the scale boundary, increment the scale
        } else if (
            currentP.mul(newProductFactor).div(DECIMAL_PRECISION) < SCALE_FACTOR
        ) {
            newP = currentP.mul(newProductFactor).mul(SCALE_FACTOR).div(
                DECIMAL_PRECISION
            );
            currentScale = currentScaleCached.add(1);
            emit ScaleUpdated(currentScale);
        } else {
            newP = currentP.mul(newProductFactor).div(DECIMAL_PRECISION);
        }

        assert(newP > 0);
        P = newP;

        emit P_Updated(newP);
    }

    function _moveOffsetCollAndDebt(
        uint _collToAdd,
        uint _debtToOffset
    ) internal {
        IActivePool activePoolCached = activePool;

        // Cancel the liquidated BaseFeeLMA debt with the BaseFeeLMA in the stability pool
        activePoolCached.decreaseBaseFeeLMADebt(_debtToOffset);
        _decreaseBaseFeeLMA(_debtToOffset);

        // Burn the debt that was successfully offset
        baseFeeLMAToken.burn(address(this), _debtToOffset);

        _increaseBalance(_collToAdd);
        activePoolCached.sendWStETH(address(this), _collToAdd);
    }

    function _decreaseBaseFeeLMA(uint _amount) internal {
        uint newTotalBaseFeeLMADeposits = totalBaseFeeLMADeposits.sub(_amount);
        totalBaseFeeLMADeposits = newTotalBaseFeeLMADeposits;
        emit StabilityPoolBaseFeeLMABalanceUpdated(newTotalBaseFeeLMADeposits);
    }

    // --- Reward calculator functions for depositor and front end ---

    /* Calculates the WStETH gain earned by the deposit since its last snapshots were taken.
     * Given by the formula:  E = d0 * (S - S(0))/P(0)
     * where S(0) and P(0) are the depositor's snapshots of the sum S and product P, respectively.
     * d0 is the last recorded deposit value.
     */
    function getDepositorWStETHGain(
        address _depositor
    ) public view returns (uint) {
        uint initialDeposit = deposits[_depositor].initialValue;

        if (initialDeposit == 0) {
            return 0;
        }

        Snapshots memory snapshots = depositSnapshots[_depositor];

        uint WStETHGain = _getWStETHGainFromSnapshots(
            initialDeposit,
            snapshots
        );
        return WStETHGain;
    }

    function _getWStETHGainFromSnapshots(
        uint initialDeposit,
        Snapshots memory snapshots
    ) internal view returns (uint) {
        /*
         * Grab the sum 'S' from the epoch at which the stake was made. The WStETH gain may span up to one scale change.
         * If it does, the second portion of the WStETH gain is scaled by 1e9.
         * If the gain spans no scale change, the second portion will be 0.
         */
        uint128 epochSnapshot = snapshots.epoch;
        uint128 scaleSnapshot = snapshots.scale;
        uint S_Snapshot = snapshots.S;
        uint P_Snapshot = snapshots.P;

        uint firstPortion = epochToScaleToSum[epochSnapshot][scaleSnapshot].sub(
            S_Snapshot
        );
        uint secondPortion = epochToScaleToSum[epochSnapshot][
            scaleSnapshot.add(1)
        ].div(SCALE_FACTOR);

        uint WStETHGain = initialDeposit
            .mul(firstPortion.add(secondPortion))
            .div(P_Snapshot)
            .div(DECIMAL_PRECISION);

        return WStETHGain;
    }

    /*
     * Calculate the HOG gain earned by a deposit since its last snapshots were taken.
     * Given by the formula:  HOG = d0 * (G - G(0))/P(0)
     * where G(0) and P(0) are the depositor's snapshots of the sum G and product P, respectively.
     * d0 is the last recorded deposit value.
     */
    function getDepositorHOGGain(
        address _depositor
    ) public view returns (uint) {
        uint initialDeposit = deposits[_depositor].initialValue;
        if (initialDeposit == 0) {
            return 0;
        }

        Snapshots memory snapshots = depositSnapshots[_depositor];

        uint HOGGain = _getHOGGainFromSnapshots(initialDeposit, snapshots);

        return HOGGain;
    }

    function _getHOGGainFromSnapshots(
        uint initialStake,
        Snapshots memory snapshots
    ) internal view returns (uint) {
        /*
         * Grab the sum 'G' from the epoch at which the stake was made. The HOG gain may span up to one scale change.
         * If it does, the second portion of the HOG gain is scaled by 1e9.
         * If the gain spans no scale change, the second portion will be 0.
         */
        uint128 epochSnapshot = snapshots.epoch;
        uint128 scaleSnapshot = snapshots.scale;
        uint G_Snapshot = snapshots.G;
        uint P_Snapshot = snapshots.P;

        uint firstPortion = epochToScaleToG[epochSnapshot][scaleSnapshot].sub(
            G_Snapshot
        );
        uint secondPortion = epochToScaleToG[epochSnapshot][
            scaleSnapshot.add(1)
        ].div(SCALE_FACTOR);

        uint HOGGain = initialStake
            .mul(firstPortion.add(secondPortion))
            .div(P_Snapshot)
            .div(DECIMAL_PRECISION);

        return HOGGain;
    }

    // --- Compounded deposit and compounded front end stake ---

    /*
     * Return the user's compounded deposit. Given by the formula:  d = d0 * P/P(0)
     * where P(0) is the depositor's snapshot of the product P, taken when they last updated their deposit.
     */
    function getCompoundedBaseFeeLMADeposit(
        address _depositor
    ) public view returns (uint) {
        uint initialDeposit = deposits[_depositor].initialValue;
        if (initialDeposit == 0) {
            return 0;
        }

        Snapshots memory snapshots = depositSnapshots[_depositor];

        uint compoundedDeposit = _getCompoundedStakeFromSnapshots(
            initialDeposit,
            snapshots
        );
        return compoundedDeposit;
    }

    // Internal function, used to calculcate compounded deposits and compounded front end stakes.
    function _getCompoundedStakeFromSnapshots(
        uint initialStake,
        Snapshots memory snapshots
    ) internal view returns (uint) {
        uint snapshot_P = snapshots.P;
        uint128 scaleSnapshot = snapshots.scale;
        uint128 epochSnapshot = snapshots.epoch;

        // If stake was made before a pool-emptying event, then it has been fully cancelled with debt -- so, return 0
        if (epochSnapshot < currentEpoch) {
            return 0;
        }

        uint compoundedStake;
        uint128 scaleDiff = currentScale.sub(scaleSnapshot);

        /* Compute the compounded stake. If a scale change in P was made during the stake's lifetime,
         * account for it. If more than one scale change was made, then the stake has decreased by a factor of
         * at least 1e-9 -- so return 0.
         */
        if (scaleDiff == 0) {
            compoundedStake = initialStake.mul(P).div(snapshot_P);
        } else if (scaleDiff == 1) {
            compoundedStake = initialStake.mul(P).div(snapshot_P).div(
                SCALE_FACTOR
            );
        } else {
            // if scaleDiff >= 2
            compoundedStake = 0;
        }

        /*
         * If compounded deposit is less than a billionth of the initial deposit, return 0.
         *
         * NOTE: originally, this line was in place to stop rounding errors making the deposit too large. However, the error
         * corrections should ensure the error in P "favors the Pool", i.e. any given compounded deposit should slightly less
         * than it's theoretical value.
         *
         * Thus it's unclear whether this line is still really needed.
         */
        if (compoundedStake < initialStake.div(1e9)) {
            return 0;
        }

        return compoundedStake;
    }

    // --- Sender functions for BaseFeeLMA deposit, WStETH gains and HOG gains ---

    // Transfer the BaseFeeLMA tokens from the user to the Stability Pool's address, and update its recorded BaseFeeLMA
    function _sendBaseFeeLMAtoStabilityPool(
        address _address,
        uint _amount
    ) internal {
        baseFeeLMAToken.sendToPool(_address, address(this), _amount);
        uint newTotalBaseFeeLMADeposits = totalBaseFeeLMADeposits.add(_amount);
        totalBaseFeeLMADeposits = newTotalBaseFeeLMADeposits;
        emit StabilityPoolBaseFeeLMABalanceUpdated(newTotalBaseFeeLMADeposits);
    }

    /**
     * HEDGEHOG UPDATES: use SafeERC20 safe transfer instead of native token transfer
     */
    function _sendWStETHGainToDepositor(uint _amount) internal {
        if (_amount == 0) {
            return;
        }
        uint newWStETH = WStETH.sub(_amount);
        WStETH = newWStETH;
        emit StabilityPoolWStETHBalanceUpdated(newWStETH);
        emit WStETHSent(msg.sender, _amount);

        WStETHToken.safeTransfer(msg.sender, _amount);
    }

    // Send BaseFeeLMA to user and decrease BaseFeeLMA in Pool
    function _sendBaseFeeLMAToDepositor(
        address _depositor,
        uint BaseFeeLMAWithdrawal
    ) internal {
        if (BaseFeeLMAWithdrawal == 0) {
            return;
        }

        baseFeeLMAToken.returnFromPool(
            address(this),
            _depositor,
            BaseFeeLMAWithdrawal
        );
        _decreaseBaseFeeLMA(BaseFeeLMAWithdrawal);
    }

    // --- Stability Pool Deposit Functionality ---
    function _updateDepositAndSnapshots(
        address _depositor,
        uint _newValue
    ) internal {
        deposits[_depositor].initialValue = _newValue;

        if (_newValue == 0) {
            delete depositSnapshots[_depositor];
            emit DepositSnapshotUpdated(_depositor, 0, 0, 0);
            return;
        }
        uint128 currentScaleCached = currentScale;
        uint128 currentEpochCached = currentEpoch;
        uint currentP = P;

        // Get S and G for the current epoch and current scale
        uint currentS = epochToScaleToSum[currentEpochCached][
            currentScaleCached
        ];
        uint currentG = epochToScaleToG[currentEpochCached][currentScaleCached];

        // Record new snapshots of the latest running product P, sum S, and sum G, for the depositor
        depositSnapshots[_depositor].P = currentP;
        depositSnapshots[_depositor].S = currentS;
        depositSnapshots[_depositor].G = currentG;
        depositSnapshots[_depositor].scale = currentScaleCached;
        depositSnapshots[_depositor].epoch = currentEpochCached;

        emit DepositSnapshotUpdated(_depositor, currentP, currentS, currentG);
    }

    function _payOutHOGGains(
        ICommunityIssuance _communityIssuance,
        address _depositor
    ) internal {
        // Pay out depositor's HOG gain
        uint depositorHOGGain = getDepositorHOGGain(_depositor);
        _communityIssuance.sendHOG(_depositor, depositorHOGGain);
        emit HOGPaidToDepositor(_depositor, depositorHOGGain);
    }

    // --- 'require' functions ---

    function _requireCallerIsActivePool() internal view {
        require(
            msg.sender == address(activePool),
            "StabilityPool: Caller is not ActivePool"
        );
    }

    function _requireCallerIsTroveManager() internal view {
        require(
            msg.sender == address(troveManager),
            "StabilityPool: Caller is not TroveManager"
        );
    }

    function _requireNoUnderCollateralizedTroves() internal {
        uint price = priceFeed.fetchPrice();
        address lowestTrove = sortedTroves.getLast();
        uint ICR = troveManager.getCurrentICR(lowestTrove, price);
        require(
            ICR >= MCR,
            "StabilityPool: Cannot withdraw while there are troves with ICR < MCR"
        );
    }

    function _requireUserHasDeposit(uint _initialDeposit) internal pure {
        require(
            _initialDeposit > 0,
            "StabilityPool: User must have a non-zero deposit"
        );
    }

    function _requireUserHasNoDeposit(address _address) internal view {
        uint initialDeposit = deposits[_address].initialValue;
        require(
            initialDeposit == 0,
            "StabilityPool: User must have no deposit"
        );
    }

    function _requireNonZeroAmount(uint _amount) internal pure {
        require(_amount > 0, "StabilityPool: Amount must be non-zero");
    }

    function _requireUserHasTrove(address _depositor) internal view {
        require(
            troveManager.getTroveStatus(_depositor) == 1,
            "StabilityPool: caller must have an active trove to withdraw WStETHGain to"
        );
    }

    function _requireUserHasWStETHGain(address _depositor) internal view {
        uint WStETHGain = getDepositorWStETHGain(_depositor);
        require(
            WStETHGain > 0,
            "StabilityPool: caller must have non-zero WStETH Gain"
        );
    }

    function _requireValidKickbackRate(uint _kickbackRate) internal pure {
        require(
            _kickbackRate <= DECIMAL_PRECISION,
            "StabilityPool: Kickback rate must be in range [0,1]"
        );
    }

    /**
     * Hedgehog Updates:
     * Remove native token fallback function and replace with internal balance increaser as it is used only in the offset function
     *
     */
    function _increaseBalance(uint256 _amount) internal {
        WStETH = WStETH.add(_amount);
    }

    // --- Fallback function ---
}

contracts/TroveManager.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity 0.8.19;

import "./interfaces/ITroveManager.sol";
import "./interfaces/IStabilityPool.sol";
import "./interfaces/ICollSurplusPool.sol";
import "./interfaces/IBaseFeeLMAToken.sol";
import "./interfaces/ISortedTroves.sol";
import "./interfaces/IHOGToken.sol";
import "./interfaces/IFeesRouter.sol";
import "./dependencies/HedgehogBase.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./dependencies/CheckContract.sol";

/**
 * @notice Fork of Liquity's TroveManager. Most of the Logic remains unchanged.
 * Changes to the contract:
 * - Raised pragma version
 * - Removed an import of ActivePool Interface
 * - Logic updates with redemption & borrowing fees calculation and their distribution
 * Even though SafeMath is no longer required, the decision was made to keep it to avoid human factor errors
 */

contract TroveManager is HedgehogBase, Ownable, CheckContract {
    using SafeMath for uint256;
    string public constant NAME = "TroveManager";

    // --- Connected contract declarations ---

    address public borrowerOperationsAddress;

    IStabilityPool public stabilityPool;

    address gasPoolAddress;

    ICollSurplusPool collSurplusPool;

    IBaseFeeLMAToken public baseFeeLMAToken;

    IHOGToken public hogToken;

    IFeesRouter public feesRouter;

    // A doubly linked list of Troves, sorted by their sorted by their collateral ratios
    ISortedTroves public sortedTroves;

    // --- Data structures ---

    uint public constant SECONDS_IN_ONE_MINUTE = 60;
    /*
     * Half-life of 12h. 12h = 720 min
     * (1/2) = d^720 => d = (1/2)^(1/720)
     */
    // HEDGEHOG UPDATES: Redemption and Borrowing Decay factors are now different variables
    uint public constant MINUTE_DECAY_REDEMPTION_FACTOR = 999037758833783000;
    uint public constant MINUTE_DECAY_BORROWING_FACTOR = 991152865945140000;
    uint public constant REDEMPTION_FEE_FLOOR = (DECIMAL_PRECISION / 1000) * 5; // 0.5%
    // HEDGEHOG UPDATES: Can reach 100% now
    uint public constant MAX_BORROWING_FEE = DECIMAL_PRECISION; // 100%

    // During bootsrap period redemptions are not allowed
    uint public immutable BOOTSTRAP_PERIOD; // 14 days for mainnet
    uint public immutable SYSTEM_DEPLOYMENT_TIME;

    /*
     * BETA: 18 digit decimal. Parameter by which to divide the redeemed fraction, in order to calc the new base rate from a redemption.
     * Corresponds to (1 / ALPHA) in the white paper.
     */
    uint public constant BETA = 2;

    // HEDGEHOG UPDATES: BaseRate is different for redemption and minting tokens
    // 1) Remove baseRate variable
    // 2) Create redemptionBaseRate public state variable
    // 3) Create borrowBaseRate public state variable
    uint public redemptionBaseRate;
    uint public borrowBaseRate;

    // HEDGEHOG UPDATES: lastFeeOperationTime is different for redemption and minting tokens
    // 1) Remove lastFeeOperationTime variable
    // 2) Create lastRedemptionTime public state variable
    // 3) Create lastBorrowTime public state variable
    uint public lastRedemptionTime;
    uint public lastBorrowTime;

    enum Status {
        nonExistent,
        active,
        closedByOwner,
        closedByLiquidation,
        closedByRedemption
    }

    // Store the necessary data for a trove
    struct Trove {
        uint debt;
        uint coll;
        uint stake;
        Status status;
        uint128 arrayIndex;
    }

    mapping(address => Trove) public Troves;

    uint public totalStakes;

    // Snapshot of the value of totalStakes, taken immediately after the latest liquidation
    uint public totalStakesSnapshot;

    // Snapshot of the total collateral across the ActivePool and DefaultPool, immediately after the latest liquidation.
    uint public totalCollateralSnapshot;

    /*
     * L_WStETH and L_BaseFeeLMADebt track the sums of accumulated liquidation rewards per unit staked. During its lifetime, each stake earns:
     *
     * An WStETH gain of ( stake * [L_WStETH - L_WStETH(0)] )
     * A BaseFeeLMADebt increase  of ( stake * [L_BaseFeeLMADebt - L_BaseFeeLMADebt(0)] )
     *
     * Where L_WStETH(0) and L_BaseFeeLMADebt(0) are snapshots of L_WStETH and L_BaseFeeLMADebt for the active Trove taken at the instant the stake was made
     */
    uint public L_WStETH;
    uint public L_BaseFeeLMADebt;

    // Map addresses with active troves to their RewardSnapshot
    mapping(address => RewardSnapshot) public rewardSnapshots;

    // Object containing the WStETH and BaseFeeLMA snapshots for a given active trove
    struct RewardSnapshot {
        uint WStETH;
        uint BaseFeeLMADebt;
    }

    // Array of all active trove addresses - used to to compute an approximate hint off-chain, for the sorted list insertion
    address[] public TroveOwners;

    // Error trackers for the trove redistribution calculation
    uint public lastWStETHError_Redistribution;
    uint public lastBaseFeeLMADebtError_Redistribution;

    /*
     * --- Variable container structs for liquidations ---
     *
     * These structs are used to hold, return and assign variables inside the liquidation functions,
     * in order to avoid the error: "CompilerError: Stack too deep".
     **/

    struct LocalVariables_OuterLiquidationFunction {
        uint price;
        uint BaseFeeLMAInStabPool;
        bool recoveryModeAtStart;
        uint liquidatedDebt;
        uint liquidatedColl;
    }

    struct LocalVariables_InnerSingleLiquidateFunction {
        uint collToLiquidate;
        uint pendingDebtReward;
        uint pendingCollReward;
    }

    struct LocalVariables_LiquidationSequence {
        uint remainingBaseFeeLMAInStabPool;
        uint i;
        uint ICR;
        address user;
        bool backToNormalMode;
        uint entireSystemDebt;
        uint entireSystemColl;
    }

    struct LiquidationValues {
        uint entireTroveDebt;
        uint entireTroveColl;
        uint collGasCompensation;
        uint BaseFeeLMAGasCompensation;
        uint debtToOffset;
        uint collToSendToSP;
        uint debtToRedistribute;
        uint collToRedistribute;
        uint collSurplus;
    }

    struct LiquidationTotals {
        uint totalCollInSequence;
        uint totalDebtInSequence;
        uint totalCollGasCompensation;
        uint totalBaseFeeLMAGasCompensation;
        uint totalDebtToOffset;
        uint totalCollToSendToSP;
        uint totalDebtToRedistribute;
        uint totalCollToRedistribute;
        uint totalCollSurplus;
    }

    struct ContractsCache {
        IActivePool activePool;
        IDefaultPool defaultPool;
        IBaseFeeLMAToken baseFeeLMAToken;
        ISortedTroves sortedTroves;
        ICollSurplusPool collSurplusPool;
        address gasPoolAddress;
    }
    // --- Variable container structs for redemptions ---

    struct RedemptionTotals {
        uint remainingBaseFeeLMA;
        uint totalBaseFeeLMAToRedeem;
        uint totalWStETHDrawn;
        uint WStETHFee;
        uint WStETHToSendToRedeemer;
        // HEDGEHOG UPDATES: BaseRate is different for redemption and minting tokens
        // Rename decayedBaseRate into decayedRedemptionBaseRate
        uint decayedRedemptionBaseRate;
        uint price;
        uint totalBaseFeeLMASupplyAtStart;
    }

    struct SingleRedemptionValues {
        uint BaseFeeLMALot;
        uint WStETHLot;
        bool cancelledPartial;
    }

    // --- Events ---

    event BorrowerOperationsAddressChanged(
        address _newBorrowerOperationsAddress
    );
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event BaseFeeLMATokenAddressChanged(address _newBaseFeeLMATokenAddress);
    event ActivePoolAddressChanged(address _activePoolAddress);
    event DefaultPoolAddressChanged(address _defaultPoolAddress);
    event StabilityPoolAddressChanged(address _stabilityPoolAddress);
    event GasPoolAddressChanged(address _gasPoolAddress);
    event CollSurplusPoolAddressChanged(address _collSurplusPoolAddress);
    event SortedTrovesAddressChanged(address _sortedTrovesAddress);
    event HOGTokenAddressChanged(address _hogTokenAddress);
    event FeesRouterAddressUpdated(IFeesRouter _feesRouter);

    event Liquidation(
        uint _liquidatedDebt,
        uint _liquidatedColl,
        uint _collGasCompensation,
        uint _BaseFeeLMAGasCompensation
    );
    event Redemption(
        uint _attemptedBaseFeeLMAAmount,
        uint _actualBaseFeeLMAAmount,
        uint _WStETHSent,
        uint _WStETHFee
    );
    event TroveUpdated(
        address indexed _borrower,
        uint _debt,
        uint _coll,
        uint _stake,
        TroveManagerOperation _operation
    );
    event TroveLiquidated(
        address indexed _borrower,
        uint _debt,
        uint _coll,
        TroveManagerOperation _operation
    );

    // HEDGEHOG UPDATES: BaseRate is different for redemption and minting tokens
    // 1) Remove BaseRateUpdated event
    // 2) Create RedemptionBaseRateUpdated event that accepts _redemptionBaseRate
    // 3) Create BorrowBaseRateUpdated event that accepts _borrowBaseRate
    event RedemptionBaseRateUpdated(uint _redemptionBaseRate);
    event BorrowBaseRateUpdated(uint _borrowBaseRate);

    // HEDGEHOG UPDATES: BaseRate is different for redemption and minting tokens
    // 1) Remove LastFeeOpTimeUpdated event
    // 2) Create LastRedemptionTimeUpdated event that accepts _lastRedemptionTime
    // 3) Create LastBorrowTimeUpdated event that accepts _lastBorrowTime
    event LastRedemptionTimeUpdated(uint _lastRedemptionTime);
    event LastBorrowTimeUpdated(uint _lastBorrowTime);
    event TotalStakesUpdated(uint _newTotalStakes);
    event SystemSnapshotsUpdated(
        uint _totalStakesSnapshot,
        uint _totalCollateralSnapshot
    );
    event LTermsUpdated(uint _L_WStETH, uint _L_BaseFeeLMADebt);
    event TroveSnapshotsUpdated(uint _L_WStETH, uint _L_BaseFeeLMADebt);
    event TroveIndexUpdated(address _borrower, uint _newIndex);

    enum TroveManagerOperation {
        applyPendingRewards,
        liquidateInNormalMode,
        liquidateInRecoveryMode,
        redeemCollateral
    }

    constructor(
        uint _gasComp,
        uint _minNetDebt,
        uint _CCR,
        uint256 _bootsrapDaysAmount
    ) HedgehogBase(_gasComp, _minNetDebt, _CCR) {
        BOOTSTRAP_PERIOD = _bootsrapDaysAmount * 60 * 60 * 24;
        SYSTEM_DEPLOYMENT_TIME = block.timestamp;
    }

    // --- Dependency setter ---

    function setAddresses(
        address _borrowerOperationsAddress,
        address _activePoolAddress,
        address _defaultPoolAddress,
        address _stabilityPoolAddress,
        address _gasPoolAddress,
        address _collSurplusPoolAddress,
        address _priceFeedAddress,
        address _baseFeeLMATokenAddress,
        address _sortedTrovesAddress,
        address _hogTokenAddress,
        IFeesRouter _feesRouterAddress
    ) external onlyOwner {
        checkContract(_borrowerOperationsAddress);
        checkContract(_activePoolAddress);
        checkContract(_defaultPoolAddress);
        checkContract(_stabilityPoolAddress);
        checkContract(_gasPoolAddress);
        checkContract(_collSurplusPoolAddress);
        checkContract(_priceFeedAddress);
        checkContract(_baseFeeLMATokenAddress);
        checkContract(_sortedTrovesAddress);
        checkContract(_hogTokenAddress);
        checkContract(address(_feesRouterAddress));

        borrowerOperationsAddress = _borrowerOperationsAddress;
        activePool = IActivePool(_activePoolAddress);
        defaultPool = IDefaultPool(_defaultPoolAddress);
        stabilityPool = IStabilityPool(_stabilityPoolAddress);
        gasPoolAddress = _gasPoolAddress;
        collSurplusPool = ICollSurplusPool(_collSurplusPoolAddress);
        priceFeed = IPriceFeed(_priceFeedAddress);
        baseFeeLMAToken = IBaseFeeLMAToken(_baseFeeLMATokenAddress);
        sortedTroves = ISortedTroves(_sortedTrovesAddress);
        hogToken = IHOGToken(_hogTokenAddress);
        feesRouter = IFeesRouter(_feesRouterAddress);

        emit BorrowerOperationsAddressChanged(_borrowerOperationsAddress);
        emit ActivePoolAddressChanged(_activePoolAddress);
        emit DefaultPoolAddressChanged(_defaultPoolAddress);
        emit StabilityPoolAddressChanged(_stabilityPoolAddress);
        emit GasPoolAddressChanged(_gasPoolAddress);
        emit CollSurplusPoolAddressChanged(_collSurplusPoolAddress);
        emit PriceFeedAddressChanged(_priceFeedAddress);
        emit BaseFeeLMATokenAddressChanged(_baseFeeLMATokenAddress);
        emit SortedTrovesAddressChanged(_sortedTrovesAddress);
        emit HOGTokenAddressChanged(_hogTokenAddress);
        emit FeesRouterAddressUpdated(_feesRouterAddress);

        renounceOwnership();
    }

    // --- Getters ---

    function getTroveOwnersCount() external view returns (uint) {
        return TroveOwners.length;
    }

    function getTroveFromTroveOwnersArray(
        uint _index
    ) external view returns (address) {
        return TroveOwners[_index];
    }

    // --- Trove Liquidation functions ---

    // Single liquidation function. Closes the trove if its ICR is lower than the minimum collateral ratio.
    function liquidate(address _borrower) external {
        _requireTroveIsActive(_borrower);

        address[] memory borrowers = new address[](1);
        borrowers[0] = _borrower;
        batchLiquidateTroves(borrowers);
    }

    // --- Inner single liquidation functions ---

    // Liquidate one trove, in Normal Mode.
    function _liquidateNormalMode(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        address _borrower,
        uint _BaseFeeLMAInStabPool
    ) internal returns (LiquidationValues memory singleLiquidation) {
        LocalVariables_InnerSingleLiquidateFunction memory vars;

        (
            singleLiquidation.entireTroveDebt,
            singleLiquidation.entireTroveColl,
            vars.pendingDebtReward,
            vars.pendingCollReward
        ) = getEntireDebtAndColl(_borrower);

        _movePendingTroveRewardsToActivePool(
            _activePool,
            _defaultPool,
            vars.pendingDebtReward,
            vars.pendingCollReward
        );
        _removeStake(_borrower);

        singleLiquidation.collGasCompensation = _getCollGasCompensation(
            singleLiquidation.entireTroveColl
        );
        singleLiquidation
            .BaseFeeLMAGasCompensation = BaseFeeLMA_GAS_COMPENSATION;
        uint collToLiquidate = singleLiquidation.entireTroveColl.sub(
            singleLiquidation.collGasCompensation
        );

        (
            singleLiquidation.debtToOffset,
            singleLiquidation.collToSendToSP,
            singleLiquidation.debtToRedistribute,
            singleLiquidation.collToRedistribute
        ) = _getOffsetAndRedistributionVals(
            singleLiquidation.entireTroveDebt,
            collToLiquidate,
            _BaseFeeLMAInStabPool
        );

        _closeTrove(_borrower, Status.closedByLiquidation);
        emit TroveLiquidated(
            _borrower,
            singleLiquidation.entireTroveDebt,
            singleLiquidation.entireTroveColl,
            TroveManagerOperation.liquidateInNormalMode
        );
        emit TroveUpdated(
            _borrower,
            0,
            0,
            0,
            TroveManagerOperation.liquidateInNormalMode
        );
        return singleLiquidation;
    }

    // Liquidate one trove, in Recovery Mode.
    function _liquidateRecoveryMode(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        address _borrower,
        uint _ICR,
        uint _BaseFeeLMAInStabPool,
        uint _TCR,
        uint _price
    ) internal returns (LiquidationValues memory singleLiquidation) {
        LocalVariables_InnerSingleLiquidateFunction memory vars;
        if (TroveOwners.length <= 1) {
            return singleLiquidation;
        } // don't liquidate if last trove
        (
            singleLiquidation.entireTroveDebt,
            singleLiquidation.entireTroveColl,
            vars.pendingDebtReward,
            vars.pendingCollReward
        ) = getEntireDebtAndColl(_borrower);

        singleLiquidation.collGasCompensation = _getCollGasCompensation(
            singleLiquidation.entireTroveColl
        );
        singleLiquidation
            .BaseFeeLMAGasCompensation = BaseFeeLMA_GAS_COMPENSATION;
        vars.collToLiquidate = singleLiquidation.entireTroveColl.sub(
            singleLiquidation.collGasCompensation
        );

        // If ICR <= 100%, purely redistribute the Trove across all active Troves
        if (_ICR <= _100pct) {
            _movePendingTroveRewardsToActivePool(
                _activePool,
                _defaultPool,
                vars.pendingDebtReward,
                vars.pendingCollReward
            );
            _removeStake(_borrower);

            singleLiquidation.debtToOffset = 0;
            singleLiquidation.collToSendToSP = 0;
            singleLiquidation.debtToRedistribute = singleLiquidation
                .entireTroveDebt;
            singleLiquidation.collToRedistribute = vars.collToLiquidate;

            _closeTrove(_borrower, Status.closedByLiquidation);
            emit TroveLiquidated(
                _borrower,
                singleLiquidation.entireTroveDebt,
                singleLiquidation.entireTroveColl,
                TroveManagerOperation.liquidateInRecoveryMode
            );
            emit TroveUpdated(
                _borrower,
                0,
                0,
                0,
                TroveManagerOperation.liquidateInRecoveryMode
            );

            // If 100% < ICR < MCR, offset as much as possible, and redistribute the remainder
        } else if ((_ICR > _100pct) && (_ICR < MCR)) {
            _movePendingTroveRewardsToActivePool(
                _activePool,
                _defaultPool,
                vars.pendingDebtReward,
                vars.pendingCollReward
            );
            _removeStake(_borrower);

            (
                singleLiquidation.debtToOffset,
                singleLiquidation.collToSendToSP,
                singleLiquidation.debtToRedistribute,
                singleLiquidation.collToRedistribute
            ) = _getOffsetAndRedistributionVals(
                singleLiquidation.entireTroveDebt,
                vars.collToLiquidate,
                _BaseFeeLMAInStabPool
            );

            _closeTrove(_borrower, Status.closedByLiquidation);
            emit TroveLiquidated(
                _borrower,
                singleLiquidation.entireTroveDebt,
                singleLiquidation.entireTroveColl,
                TroveManagerOperation.liquidateInRecoveryMode
            );
            emit TroveUpdated(
                _borrower,
                0,
                0,
                0,
                TroveManagerOperation.liquidateInRecoveryMode
            );
            /*
             * If 110% <= ICR < current TCR (accounting for the preceding liquidations in the current sequence)
             * and there is BaseFeeLMA in the Stability Pool, only offset, with no redistribution,
             * but at a capped rate of 1.1 and only if the whole debt can be liquidated.
             * The remainder due to the capped rate will be claimable as collateral surplus.
             */
        } else if (
            (_ICR >= MCR) &&
            (_ICR < _TCR) &&
            (singleLiquidation.entireTroveDebt <= _BaseFeeLMAInStabPool)
        ) {
            _movePendingTroveRewardsToActivePool(
                _activePool,
                _defaultPool,
                vars.pendingDebtReward,
                vars.pendingCollReward
            );
            assert(_BaseFeeLMAInStabPool != 0);

            _removeStake(_borrower);
            singleLiquidation = _getCappedOffsetVals(
                singleLiquidation.entireTroveDebt,
                singleLiquidation.entireTroveColl,
                _price
            );

            _closeTrove(_borrower, Status.closedByLiquidation);
            if (singleLiquidation.collSurplus > 0) {
                collSurplusPool.accountSurplus(
                    _borrower,
                    singleLiquidation.collSurplus
                );
            }

            emit TroveLiquidated(
                _borrower,
                singleLiquidation.entireTroveDebt,
                singleLiquidation.collToSendToSP,
                TroveManagerOperation.liquidateInRecoveryMode
            );
            emit TroveUpdated(
                _borrower,
                0,
                0,
                0,
                TroveManagerOperation.liquidateInRecoveryMode
            );
        } else {
            // if (_ICR >= MCR && ( _ICR >= _TCR || singleLiquidation.entireTroveDebt > _BaseFeeLMAInStabPool))
            LiquidationValues memory zeroVals;
            return zeroVals;
        }

        return singleLiquidation;
    }

    /* In a full liquidation, returns the values for a trove's coll and debt to be offset, and coll and debt to be
     * redistributed to active troves.
     */
    function _getOffsetAndRedistributionVals(
        uint _debt,
        uint _coll,
        uint _BaseFeeLMAInStabPool
    )
        internal
        pure
        returns (
            uint debtToOffset,
            uint collToSendToSP,
            uint debtToRedistribute,
            uint collToRedistribute
        )
    {
        if (_BaseFeeLMAInStabPool > 0) {
            /*
             * Offset as much debt & collateral as possible against the Stability Pool, and redistribute the remainder
             * between all active troves.
             *
             *  If the trove's debt is larger than the deposited BaseFeeLMA in the Stability Pool:
             *
             *  - Offset an amount of the trove's debt equal to the BaseFeeLMA in the Stability Pool
             *  - Send a fraction of the trove's collateral to the Stability Pool, equal to the fraction of its offset debt
             *
             */
            debtToOffset = LiquityMath._min(_debt, _BaseFeeLMAInStabPool);
            collToSendToSP = _coll.mul(debtToOffset).div(_debt);
            debtToRedistribute = _debt.sub(debtToOffset);
            collToRedistribute = _coll.sub(collToSendToSP);
        } else {
            debtToOffset = 0;
            collToSendToSP = 0;
            debtToRedistribute = _debt;
            collToRedistribute = _coll;
        }
    }

    /*
     *  Get its offset coll/debt and WStETH gas comp, and close the trove.
     */
    function _getCappedOffsetVals(
        uint _entireTroveDebt,
        uint _entireTroveColl,
        uint _price
    ) internal view returns (LiquidationValues memory singleLiquidation) {
        singleLiquidation.entireTroveDebt = _entireTroveDebt;
        singleLiquidation.entireTroveColl = _entireTroveColl;

        // HEDGEHOG UPDATES:
        // Changed the cappedCollPortion formula from [entireTroveDebt] * [MCR] / [price]  to => [entireTroveDebt] * [MCR] * [price] / [DECIMAL_PRECISION]/ [DECIMAL_PRECISION]
        uint cappedCollPortion = _entireTroveDebt.mul(MCR).mul(_price).div(
            DECIMAL_PRECISION
        );

        singleLiquidation.collGasCompensation = _getCollGasCompensation(
            cappedCollPortion
        );
        singleLiquidation
            .BaseFeeLMAGasCompensation = BaseFeeLMA_GAS_COMPENSATION;

        singleLiquidation.debtToOffset = _entireTroveDebt;
        singleLiquidation.collToSendToSP = cappedCollPortion.sub(
            singleLiquidation.collGasCompensation
        );
        singleLiquidation.collSurplus = _entireTroveColl.sub(cappedCollPortion);
        singleLiquidation.debtToRedistribute = 0;
        singleLiquidation.collToRedistribute = 0;
    }

    /*
     * Liquidate a sequence of troves. Closes a maximum number of n under-collateralized Troves,
     * starting from the one with the lowest collateral ratio in the system, and moving upwards
     */
    function liquidateTroves(uint _n) external {
        ContractsCache memory contractsCache = ContractsCache(
            activePool,
            defaultPool,
            IBaseFeeLMAToken(address(0)),
            sortedTroves,
            ICollSurplusPool(address(0)),
            address(0)
        );
        IStabilityPool stabilityPoolCached = stabilityPool;

        LocalVariables_OuterLiquidationFunction memory vars;

        LiquidationTotals memory totals;

        vars.price = priceFeed.fetchPrice();
        vars.BaseFeeLMAInStabPool = stabilityPoolCached
            .getTotalBaseFeeLMADeposits();
        vars.recoveryModeAtStart = _checkRecoveryMode(vars.price);

        // Perform the appropriate liquidation sequence - tally the values, and obtain their totals
        if (vars.recoveryModeAtStart) {
            totals = _getTotalsFromLiquidateTrovesSequence_RecoveryMode(
                contractsCache,
                vars.price,
                vars.BaseFeeLMAInStabPool,
                _n
            );
        } else {
            // if !vars.recoveryModeAtStart
            totals = _getTotalsFromLiquidateTrovesSequence_NormalMode(
                contractsCache.activePool,
                contractsCache.defaultPool,
                vars.price,
                vars.BaseFeeLMAInStabPool,
                _n
            );
        }

        require(
            totals.totalDebtInSequence > 0,
            "TroveManager: nothing to liquidate"
        );

        // Move liquidated WStETH and BaseFeeLMA to the appropriate pools
        stabilityPoolCached.offset(
            totals.totalDebtToOffset,
            totals.totalCollToSendToSP
        );
        _redistributeDebtAndColl(
            contractsCache.activePool,
            contractsCache.defaultPool,
            totals.totalDebtToRedistribute,
            totals.totalCollToRedistribute
        );
        if (totals.totalCollSurplus > 0) {
            contractsCache.activePool.sendWStETH(
                address(collSurplusPool),
                totals.totalCollSurplus
            );
        }

        // Update system snapshots
        _updateSystemSnapshots_excludeCollRemainder(
            contractsCache.activePool,
            totals.totalCollGasCompensation
        );

        vars.liquidatedDebt = totals.totalDebtInSequence;
        vars.liquidatedColl = totals
            .totalCollInSequence
            .sub(totals.totalCollGasCompensation)
            .sub(totals.totalCollSurplus);
        emit Liquidation(
            vars.liquidatedDebt,
            vars.liquidatedColl,
            totals.totalCollGasCompensation,
            totals.totalBaseFeeLMAGasCompensation
        );

        // Send gas compensation to caller
        _sendGasCompensation(
            contractsCache.activePool,
            msg.sender,
            totals.totalBaseFeeLMAGasCompensation,
            totals.totalCollGasCompensation
        );
    }

    /*
     * This function is used when the liquidateTroves sequence starts during Recovery Mode. However, it
     * handle the case where the system *leaves* Recovery Mode, part way through the liquidation sequence
     */
    function _getTotalsFromLiquidateTrovesSequence_RecoveryMode(
        ContractsCache memory _contractsCache,
        uint _price,
        uint _BaseFeeLMAInStabPool,
        uint _n
    ) internal returns (LiquidationTotals memory totals) {
        LocalVariables_LiquidationSequence memory vars;
        LiquidationValues memory singleLiquidation;

        vars.remainingBaseFeeLMAInStabPool = _BaseFeeLMAInStabPool;
        vars.backToNormalMode = false;
        vars.entireSystemDebt = getEntireSystemDebt();
        vars.entireSystemColl = getEntireSystemColl();

        vars.user = _contractsCache.sortedTroves.getLast();
        address firstUser = _contractsCache.sortedTroves.getFirst();
        for (vars.i = 0; vars.i < _n && vars.user != firstUser; vars.i++) {
            // we need to cache it, because current user is likely going to be deleted
            address nextUser = _contractsCache.sortedTroves.getPrev(vars.user);

            vars.ICR = getCurrentICR(vars.user, _price);

            if (!vars.backToNormalMode) {
                // Break the loop if ICR is greater than MCR and Stability Pool is empty
                if (
                    vars.ICR >= MCR && vars.remainingBaseFeeLMAInStabPool == 0
                ) {
                    break;
                }

                uint TCR = LiquityMath._computeCR(
                    vars.entireSystemColl,
                    vars.entireSystemDebt,
                    _price
                );

                singleLiquidation = _liquidateRecoveryMode(
                    _contractsCache.activePool,
                    _contractsCache.defaultPool,
                    vars.user,
                    vars.ICR,
                    vars.remainingBaseFeeLMAInStabPool,
                    TCR,
                    _price
                );

                // Update aggregate trackers
                vars.remainingBaseFeeLMAInStabPool = vars
                    .remainingBaseFeeLMAInStabPool
                    .sub(singleLiquidation.debtToOffset);
                vars.entireSystemDebt = vars.entireSystemDebt.sub(
                    singleLiquidation.debtToOffset
                );
                vars.entireSystemColl = vars
                    .entireSystemColl
                    .sub(singleLiquidation.collToSendToSP)
                    .sub(singleLiquidation.collGasCompensation)
                    .sub(singleLiquidation.collSurplus);

                // Add liquidation values to their respective running totals
                totals = _addLiquidationValuesToTotals(
                    totals,
                    singleLiquidation
                );

                vars.backToNormalMode = !_checkPotentialRecoveryMode(
                    vars.entireSystemColl,
                    vars.entireSystemDebt,
                    _price
                );
            } else if (vars.backToNormalMode && vars.ICR < MCR) {
                singleLiquidation = _liquidateNormalMode(
                    _contractsCache.activePool,
                    _contractsCache.defaultPool,
                    vars.user,
                    vars.remainingBaseFeeLMAInStabPool
                );

                vars.remainingBaseFeeLMAInStabPool = vars
                    .remainingBaseFeeLMAInStabPool
                    .sub(singleLiquidation.debtToOffset);

                // Add liquidation values to their respective running totals
                totals = _addLiquidationValuesToTotals(
                    totals,
                    singleLiquidation
                );
            } else break; // break if the loop reaches a Trove with ICR >= MCR

            vars.user = nextUser;
        }
    }

    function _getTotalsFromLiquidateTrovesSequence_NormalMode(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        uint _price,
        uint _BaseFeeLMAInStabPool,
        uint _n
    ) internal returns (LiquidationTotals memory totals) {
        LocalVariables_LiquidationSequence memory vars;
        LiquidationValues memory singleLiquidation;
        ISortedTroves sortedTrovesCached = sortedTroves;

        vars.remainingBaseFeeLMAInStabPool = _BaseFeeLMAInStabPool;

        for (vars.i = 0; vars.i < _n; vars.i++) {
            vars.user = sortedTrovesCached.getLast();
            vars.ICR = getCurrentICR(vars.user, _price);

            if (vars.ICR < MCR) {
                singleLiquidation = _liquidateNormalMode(
                    _activePool,
                    _defaultPool,
                    vars.user,
                    vars.remainingBaseFeeLMAInStabPool
                );

                vars.remainingBaseFeeLMAInStabPool = vars
                    .remainingBaseFeeLMAInStabPool
                    .sub(singleLiquidation.debtToOffset);

                // Add liquidation values to their respective running totals
                totals = _addLiquidationValuesToTotals(
                    totals,
                    singleLiquidation
                );
            } else break; // break if the loop reaches a Trove with ICR >= MCR
        }
    }

    /*
     * Attempt to liquidate a custom list of troves provided by the caller.
     */
    function batchLiquidateTroves(address[] memory _troveArray) public {
        require(
            _troveArray.length != 0,
            "TroveManager: Calldata address array must not be empty"
        );

        IActivePool activePoolCached = activePool;
        IDefaultPool defaultPoolCached = defaultPool;
        IStabilityPool stabilityPoolCached = stabilityPool;

        LocalVariables_OuterLiquidationFunction memory vars;
        LiquidationTotals memory totals;

        vars.price = priceFeed.fetchPrice();
        vars.BaseFeeLMAInStabPool = stabilityPoolCached
            .getTotalBaseFeeLMADeposits();
        vars.recoveryModeAtStart = _checkRecoveryMode(vars.price);

        // Perform the appropriate liquidation sequence - tally values and obtain their totals.
        if (vars.recoveryModeAtStart) {
            totals = _getTotalFromBatchLiquidate_RecoveryMode(
                activePoolCached,
                defaultPoolCached,
                vars.price,
                vars.BaseFeeLMAInStabPool,
                _troveArray
            );
        } else {
            //  if !vars.recoveryModeAtStart
            totals = _getTotalsFromBatchLiquidate_NormalMode(
                activePoolCached,
                defaultPoolCached,
                vars.price,
                vars.BaseFeeLMAInStabPool,
                _troveArray
            );
        }

        require(
            totals.totalDebtInSequence > 0,
            "TroveManager: nothing to liquidate"
        );

        // Move liquidated WStETH and BaseFeeLMA to the appropriate pools
        stabilityPoolCached.offset(
            totals.totalDebtToOffset,
            totals.totalCollToSendToSP
        );
        _redistributeDebtAndColl(
            activePoolCached,
            defaultPoolCached,
            totals.totalDebtToRedistribute,
            totals.totalCollToRedistribute
        );

        if (totals.totalCollSurplus > 0) {
            collSurplusPool.increaseBalance(totals.totalCollSurplus);
            activePoolCached.sendWStETH(
                address(collSurplusPool),
                totals.totalCollSurplus
            );
        }
        // Update system snapshots
        _updateSystemSnapshots_excludeCollRemainder(
            activePoolCached,
            totals.totalCollGasCompensation
        );

        vars.liquidatedDebt = totals.totalDebtInSequence;

        vars.liquidatedColl = totals
            .totalCollInSequence
            .sub(totals.totalCollGasCompensation)
            .sub(totals.totalCollSurplus);

        emit Liquidation(
            vars.liquidatedDebt,
            vars.liquidatedColl,
            totals.totalCollGasCompensation,
            totals.totalBaseFeeLMAGasCompensation
        );
        // Send gas compensation to caller
        _sendGasCompensation(
            activePoolCached,
            msg.sender,
            totals.totalBaseFeeLMAGasCompensation,
            totals.totalCollGasCompensation
        );
    }

    /*
     * This function is used when the batch liquidation sequence starts during Recovery Mode. However, it
     * handle the case where the system *leaves* Recovery Mode, part way through the liquidation sequence
     */
    function _getTotalFromBatchLiquidate_RecoveryMode(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        uint _price,
        uint _BaseFeeLMAInStabPool,
        address[] memory _troveArray
    ) internal returns (LiquidationTotals memory totals) {
        LocalVariables_LiquidationSequence memory vars;
        LiquidationValues memory singleLiquidation;

        vars.remainingBaseFeeLMAInStabPool = _BaseFeeLMAInStabPool;
        vars.backToNormalMode = false;
        vars.entireSystemDebt = getEntireSystemDebt();
        vars.entireSystemColl = getEntireSystemColl();

        for (vars.i = 0; vars.i < _troveArray.length; vars.i++) {
            vars.user = _troveArray[vars.i];
            // Skip non-active troves
            if (Troves[vars.user].status != Status.active) {
                continue;
            }
            vars.ICR = getCurrentICR(vars.user, _price);

            if (!vars.backToNormalMode) {
                // Skip this trove if ICR is greater than MCR and Stability Pool is empty
                if (
                    vars.ICR >= MCR && vars.remainingBaseFeeLMAInStabPool == 0
                ) {
                    continue;
                }

                uint TCR = LiquityMath._computeCR(
                    vars.entireSystemColl,
                    vars.entireSystemDebt,
                    _price
                );

                singleLiquidation = _liquidateRecoveryMode(
                    _activePool,
                    _defaultPool,
                    vars.user,
                    vars.ICR,
                    vars.remainingBaseFeeLMAInStabPool,
                    TCR,
                    _price
                );

                // Update aggregate trackers
                vars.remainingBaseFeeLMAInStabPool = vars
                    .remainingBaseFeeLMAInStabPool
                    .sub(singleLiquidation.debtToOffset);
                vars.entireSystemDebt = vars.entireSystemDebt.sub(
                    singleLiquidation.debtToOffset
                );
                vars.entireSystemColl = vars
                    .entireSystemColl
                    .sub(singleLiquidation.collToSendToSP)
                    .sub(singleLiquidation.collGasCompensation)
                    .sub(singleLiquidation.collSurplus);

                // Add liquidation values to their respective running totals
                totals = _addLiquidationValuesToTotals(
                    totals,
                    singleLiquidation
                );

                vars.backToNormalMode = !_checkPotentialRecoveryMode(
                    vars.entireSystemColl,
                    vars.entireSystemDebt,
                    _price
                );
            } else if (vars.backToNormalMode && vars.ICR < MCR) {
                singleLiquidation = _liquidateNormalMode(
                    _activePool,
                    _defaultPool,
                    vars.user,
                    vars.remainingBaseFeeLMAInStabPool
                );
                vars.remainingBaseFeeLMAInStabPool = vars
                    .remainingBaseFeeLMAInStabPool
                    .sub(singleLiquidation.debtToOffset);

                // Add liquidation values to their respective running totals
                totals = _addLiquidationValuesToTotals(
                    totals,
                    singleLiquidation
                );
            } else continue; // In Normal Mode skip troves with ICR >= MCR
        }
    }

    function _getTotalsFromBatchLiquidate_NormalMode(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        uint _price,
        uint _BaseFeeLMAInStabPool,
        address[] memory _troveArray
    ) internal returns (LiquidationTotals memory totals) {
        LocalVariables_LiquidationSequence memory vars;
        LiquidationValues memory singleLiquidation;

        vars.remainingBaseFeeLMAInStabPool = _BaseFeeLMAInStabPool;

        for (vars.i = 0; vars.i < _troveArray.length; vars.i++) {
            vars.user = _troveArray[vars.i];
            vars.ICR = getCurrentICR(vars.user, _price);

            if (vars.ICR < MCR) {
                singleLiquidation = _liquidateNormalMode(
                    _activePool,
                    _defaultPool,
                    vars.user,
                    vars.remainingBaseFeeLMAInStabPool
                );

                vars.remainingBaseFeeLMAInStabPool = vars
                    .remainingBaseFeeLMAInStabPool
                    .sub(singleLiquidation.debtToOffset);

                // Add liquidation values to their respective running totals
                totals = _addLiquidationValuesToTotals(
                    totals,
                    singleLiquidation
                );
            }
        }
    }

    // --- Liquidation helper functions ---

    function _addLiquidationValuesToTotals(
        LiquidationTotals memory oldTotals,
        LiquidationValues memory singleLiquidation
    ) internal pure returns (LiquidationTotals memory newTotals) {
        // Tally all the values with their respective running totals
        newTotals.totalCollGasCompensation = oldTotals
            .totalCollGasCompensation
            .add(singleLiquidation.collGasCompensation);
        newTotals.totalBaseFeeLMAGasCompensation = oldTotals
            .totalBaseFeeLMAGasCompensation
            .add(singleLiquidation.BaseFeeLMAGasCompensation);
        newTotals.totalDebtInSequence = oldTotals.totalDebtInSequence.add(
            singleLiquidation.entireTroveDebt
        );
        newTotals.totalCollInSequence = oldTotals.totalCollInSequence.add(
            singleLiquidation.entireTroveColl
        );
        newTotals.totalDebtToOffset = oldTotals.totalDebtToOffset.add(
            singleLiquidation.debtToOffset
        );
        newTotals.totalCollToSendToSP = oldTotals.totalCollToSendToSP.add(
            singleLiquidation.collToSendToSP
        );
        newTotals.totalDebtToRedistribute = oldTotals
            .totalDebtToRedistribute
            .add(singleLiquidation.debtToRedistribute);
        newTotals.totalCollToRedistribute = oldTotals
            .totalCollToRedistribute
            .add(singleLiquidation.collToRedistribute);
        newTotals.totalCollSurplus = oldTotals.totalCollSurplus.add(
            singleLiquidation.collSurplus
        );

        return newTotals;
    }

    function _sendGasCompensation(
        IActivePool _activePool,
        address _liquidator,
        uint _BaseFeeLMA,
        uint _WStETH
    ) internal {
        if (_BaseFeeLMA > 0) {
            baseFeeLMAToken.returnFromPool(
                gasPoolAddress,
                _liquidator,
                _BaseFeeLMA
            );
        }

        if (_WStETH > 0) {
            _activePool.sendWStETH(_liquidator, _WStETH);
        }
    }

    // Move a Trove's pending debt and collateral rewards from distributions, from the Default Pool to the Active Pool
    function _movePendingTroveRewardsToActivePool(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        uint _BaseFeeLMA,
        uint _WStETH
    ) internal {
        _defaultPool.decreaseBaseFeeLMADebt(_BaseFeeLMA);
        _activePool.increaseBaseFeeLMADebt(_BaseFeeLMA);

        _defaultPool.sendWStETHToActivePool(_WStETH);
    }

    // --- Redemption functions ---

    // Redeem as much collateral as possible from _borrower's Trove in exchange for BaseFeeLMA up to _maxBaseFeeLMAamount
    // HEDGEHOG Updates: Not subtracting gas compensation from the debt anymore
    function _redeemCollateralFromTrove(
        ContractsCache memory _contractsCache,
        address _borrower,
        uint _maxBaseFeeLMAamount,
        uint _price,
        address _upperPartialRedemptionHint,
        address _lowerPartialRedemptionHint,
        uint _partialRedemptionHintNICR
    ) internal returns (SingleRedemptionValues memory singleRedemption) {
        // Determine the remaining amount (lot) to be redeemed, capped by the entire debt of the Trove minus the liquidation reserve
        singleRedemption.BaseFeeLMALot = LiquityMath._min(
            _maxBaseFeeLMAamount,
            Troves[_borrower].debt
        );

        // Get the WStETHLot of equivalent value in USD
        // HEDGEHOG UPDATES: Change WStETHLOT calculations formula from [debtToBeRedeemed * price * 10e9] to [debtToBeRedeemed / price]
        singleRedemption.WStETHLot = singleRedemption.BaseFeeLMALot.mul(_price);

        // Decrease the debt and collateral of the current Trove according to the BaseFeeLMA lot and corresponding WStETH to send
        uint newDebt = (Troves[_borrower].debt).sub(
            singleRedemption.BaseFeeLMALot
        );
        uint newColl = (Troves[_borrower].coll).sub(singleRedemption.WStETHLot);

        if (newDebt == BaseFeeLMA_GAS_COMPENSATION) {
            // No debt left in the Trove (except for the liquidation reserve), therefore the trove gets closed
            _removeStake(_borrower);
            _closeTrove(_borrower, Status.closedByRedemption);
            _redeemCloseTrove(
                _contractsCache,
                _borrower,
                BaseFeeLMA_GAS_COMPENSATION,
                newColl
            );
            emit TroveUpdated(
                _borrower,
                0,
                0,
                0,
                TroveManagerOperation.redeemCollateral
            );
        } else {
            uint newNICR = LiquityMath._computeNominalCR(newColl, newDebt);

            /*
             * If the provided hint is out of date, we bail since trying to reinsert without a good hint will almost
             * certainly result in running out of gas.
             *
             * If the resultant net debt of the partial is less than the minimum, net debt we bail.
             */

            if (
                newNICR != _partialRedemptionHintNICR ||
                _getNetDebt(newDebt) < MIN_NET_DEBT
            ) {
                singleRedemption.cancelledPartial = true;
                return singleRedemption;
            }

            _contractsCache.sortedTroves.reInsert(
                _borrower,
                newNICR,
                _upperPartialRedemptionHint,
                _lowerPartialRedemptionHint
            );

            Troves[_borrower].debt = newDebt;
            Troves[_borrower].coll = newColl;
            _updateStakeAndTotalStakes(_borrower);

            emit TroveUpdated(
                _borrower,
                newDebt,
                newColl,
                Troves[_borrower].stake,
                TroveManagerOperation.redeemCollateral
            );
        }

        return singleRedemption;
    }

    /*
     * Called when a full redemption occurs, and closes the trove.
     * The redeemer swaps (debt - liquidation reserve) BaseFeeLMA for (debt - liquidation reserve) worth of WStETH, so the BaseFeeLMA liquidation reserve left corresponds to the remaining debt.
     * In order to close the trove, the BaseFeeLMA liquidation reserve is burned, and the corresponding debt is removed from the active pool.
     * The debt recorded on the trove's struct is zero'd elswhere, in _closeTrove.
     * Any surplus WStETH left in the trove, is sent to the Coll surplus pool, and can be later claimed by the borrower.
     */
    function _redeemCloseTrove(
        ContractsCache memory _contractsCache,
        address _borrower,
        uint _BaseFeeLMA,
        uint _WStETH
    ) internal {
        _contractsCache.baseFeeLMAToken.burn(gasPoolAddress, _BaseFeeLMA);
        // Update Active Pool BaseFeeLMA, and send WStETH to account
        _contractsCache.activePool.decreaseBaseFeeLMADebt(_BaseFeeLMA);

        // send WStETH from Active Pool to CollSurplus Pool
        _contractsCache.collSurplusPool.accountSurplus(_borrower, _WStETH);
        _contractsCache.activePool.sendWStETH(
            address(_contractsCache.collSurplusPool),
            _WStETH
        );
    }

    function _isValidFirstRedemptionHint(
        ISortedTroves _sortedTroves,
        address _firstRedemptionHint,
        uint _price
    ) internal view returns (bool) {
        if (
            _firstRedemptionHint == address(0) ||
            !_sortedTroves.contains(_firstRedemptionHint) ||
            getCurrentICR(_firstRedemptionHint, _price) < MCR
        ) {
            return false;
        }

        address nextTrove = _sortedTroves.getNext(_firstRedemptionHint);
        return
            nextTrove == address(0) || getCurrentICR(nextTrove, _price) < MCR;
    }

    /* Send _BaseFeeLMAamount BaseFeeLMA to the system and redeem the corresponding amount of collateral from as many Troves as are needed to fill the redemption
     * request.  Applies pending rewards to a Trove before reducing its debt and coll.
     *
     * Note that if _amount is very large, this function can run out of gas, specially if traversed troves are small. This can be easily avoided by
     * splitting the total _amount in appropriate chunks and calling the function multiple times.
     *
     * Param `_maxIterations` can also be provided, so the loop through Troves is capped (if its zero, it will be ignored).This makes it easier to
     * avoid OOG for the frontend, as only knowing approximately the average cost of an iteration is enough, without needing to know the topology
     * of the trove list. It also avoids the need to set the cap in stone in the contract, nor doing gas calculations, as both gas price and opcode
     * costs can vary.
     *
     * All Troves that are redeemed from -- with the likely exception of the last one -- will end up with no debt left, therefore they will be closed.
     * If the last Trove does have some remaining debt, it has a finite ICR, and the reinsertion could be anywhere in the list, therefore it requires a hint.
     * A frontend should use getRedemptionHints() to calculate what the ICR of this Trove will be after redemption, and pass a hint for its position
     * in the sortedTroves list along with the ICR value that the hint was found for.
     *
     * If another transaction modifies the list between calling getRedemptionHints() and passing the hints to redeemCollateral(), it
     * is very likely that the last (partially) redeemed Trove would end up with a different ICR than what the hint is for. In this case the
     * redemption will stop after the last completely redeemed Trove and the sender will keep the remaining BaseFeeLMA amount, which they can attempt
     * to redeem later.
     */
    function redeemCollateral(
        uint _BaseFeeLMAamount,
        address _firstRedemptionHint,
        address _upperPartialRedemptionHint,
        address _lowerPartialRedemptionHint,
        uint _partialRedemptionHintNICR,
        uint _maxIterations,
        uint _maxFeePercentage
    ) external {
        ContractsCache memory contractsCache = ContractsCache(
            activePool,
            defaultPool,
            baseFeeLMAToken,
            sortedTroves,
            collSurplusPool,
            gasPoolAddress
        );
        RedemptionTotals memory totals;

        _requireValidMaxFeePercentage(_maxFeePercentage);
        _requireAfterBootstrapPeriod();
        totals.price = priceFeed.fetchPrice();

        _requireTCRoverMCR(totals.price);
        _requireAmountGreaterThanZero(_BaseFeeLMAamount);
        _requireBaseFeeLMABalanceCoversRedemption(
            contractsCache.baseFeeLMAToken,
            msg.sender,
            _BaseFeeLMAamount
        );

        totals.totalBaseFeeLMASupplyAtStart = getEntireSystemDebt();
        // Confirm redeemer's balance is less than total BaseFeeLMA supply
        assert(
            contractsCache.baseFeeLMAToken.balanceOf(msg.sender) <=
                totals.totalBaseFeeLMASupplyAtStart
        );

        totals.remainingBaseFeeLMA = _BaseFeeLMAamount;
        address currentBorrower;

        if (
            _isValidFirstRedemptionHint(
                contractsCache.sortedTroves,
                _firstRedemptionHint,
                totals.price
            )
        ) {
            currentBorrower = _firstRedemptionHint;
        } else {
            currentBorrower = contractsCache.sortedTroves.getLast();
            // Find the first trove with ICR >= MCR
            while (
                currentBorrower != address(0) &&
                getCurrentICR(currentBorrower, totals.price) < MCR
            ) {
                currentBorrower = contractsCache.sortedTroves.getPrev(
                    currentBorrower
                );
            }
        }
        // Loop through the Troves starting from the one with lowest collateral ratio until _amount of BaseFeeLMA is exchanged for collateral
        if (_maxIterations == 0) {
            // Previous implementation: _maxIterations = uint(-1);
            // Updated since 8th pragma does not allow anymore
            _maxIterations = type(uint).max;
        }
        while (
            currentBorrower != address(0) &&
            totals.remainingBaseFeeLMA > 0 &&
            _maxIterations > 0
        ) {
            _maxIterations--;
            // Save the address of the Trove preceding the current one, before potentially modifying the list
            address nextUserToCheck = contractsCache.sortedTroves.getPrev(
                currentBorrower
            );
            _applyPendingRewards(
                contractsCache.activePool,
                contractsCache.defaultPool,
                currentBorrower
            );

            SingleRedemptionValues
                memory singleRedemption = _redeemCollateralFromTrove(
                    contractsCache,
                    currentBorrower,
                    totals.remainingBaseFeeLMA,
                    totals.price,
                    _upperPartialRedemptionHint,
                    _lowerPartialRedemptionHint,
                    _partialRedemptionHintNICR
                );

            if (singleRedemption.cancelledPartial) break; // Partial redemption was cancelled (out-of-date hint, or new net debt < minimum), therefore we could not redeem from the last Trove
            totals.totalBaseFeeLMAToRedeem = totals.totalBaseFeeLMAToRedeem.add(
                singleRedemption.BaseFeeLMALot
            );
            totals.totalWStETHDrawn = totals.totalWStETHDrawn.add(
                singleRedemption.WStETHLot
            );

            totals.remainingBaseFeeLMA = totals.remainingBaseFeeLMA.sub(
                singleRedemption.BaseFeeLMALot
            );
            currentBorrower = nextUserToCheck;
        }

        require(
            totals.totalWStETHDrawn > 0,
            "TroveManager: Unable to redeem any amount"
        );
        // HEDGEHOG LOGIC UPDATE:
        // 1) rename _updateBaseRateFromRedemption into _updateRedemptionBaseRateFromRedemption
        // 2) update commented explanation (baseRate => redemptionBaseRate)
        // Decay the redemptionBaseRate due to time passed, and then increase it according to the size of this redemption.
        // Use the saved total BaseFeeLMA supply value, from before it was reduced by the redemption.
        _updateRedemptionBaseRateFromRedemption(totals.totalWStETHDrawn);
        // Calculate the WStETH fee
        totals.WStETHFee = _getRedemptionFee(totals.totalWStETHDrawn);

        _requireUserAcceptsFee(
            totals.WStETHFee,
            totals.totalWStETHDrawn,
            _maxFeePercentage
        );

        // HEDGHEHOG UPDATES:
        // Fees are now distributed among different addresses based on how big they are
        feesRouter.distributeCollFee(totals.totalWStETHDrawn, totals.WStETHFee);

        totals.WStETHToSendToRedeemer = totals.totalWStETHDrawn.sub(
            totals.WStETHFee
        );

        emit Redemption(
            _BaseFeeLMAamount,
            totals.totalBaseFeeLMAToRedeem,
            totals.totalWStETHDrawn,
            totals.WStETHFee
        );
        // Burn the total BaseFeeLMA that is cancelled with debt, and send the redeemed WStETH to msg.sender
        contractsCache.baseFeeLMAToken.burn(
            msg.sender,
            totals.totalBaseFeeLMAToRedeem
        );
        // Update Active Pool BaseFeeLMA, and send WStETH to account
        contractsCache.activePool.decreaseBaseFeeLMADebt(
            totals.totalBaseFeeLMAToRedeem
        );

        contractsCache.activePool.sendWStETH(
            msg.sender,
            totals.WStETHToSendToRedeemer
        );
    }

    // --- Helper functions ---

    // Return the nominal collateral ratio (ICR) of a given Trove, without the price. Takes a trove's pending coll and debt rewards from redistributions into account.
    function getNominalICR(address _borrower) public view returns (uint) {
        (
            uint currentWStETH,
            uint currentBaseFeeLMADebt
        ) = _getCurrentTroveAmounts(_borrower);

        uint NICR = LiquityMath._computeNominalCR(
            currentWStETH,
            currentBaseFeeLMADebt
        );
        return NICR;
    }

    /**
     * HEDGEHOG UPDATES:
     * Get Price directly from the price feed instead of param passing
     */
    // Return the current collateral ratio (ICR) of a given Trove. Takes a trove's pending coll and debt rewards from redistributions into account.
    function getCurrentICR(
        address _borrower,
        uint _price
    ) public view returns (uint) {
        (
            uint currentWStETH,
            uint currentBaseFeeLMADebt
        ) = _getCurrentTroveAmounts(_borrower);

        uint ICR = LiquityMath._computeCR(
            currentWStETH,
            currentBaseFeeLMADebt,
            _price
        );
        return ICR;
    }

    /**
     * HEDGEHOG UPDATES:
     * New view method to help with getting the data on frontends
     */
    function getUnreliableTroveICR(
        address _borrower
    ) public view returns (uint) {
        uint256 price = priceFeed.lastGoodPrice();
        (
            uint currentWStETH,
            uint currentBaseFeeLMADebt
        ) = _getCurrentTroveAmounts(_borrower);

        uint ICR = LiquityMath._computeCR(
            currentWStETH,
            currentBaseFeeLMADebt,
            price
        );
        return ICR;
    }

    function _getCurrentTroveAmounts(
        address _borrower
    ) internal view returns (uint, uint) {
        uint pendingWStETHReward = getPendingWStETHReward(_borrower);
        uint pendingBaseFeeLMADebtReward = getPendingBaseFeeLMADebtReward(
            _borrower
        );

        uint currentWStETH = Troves[_borrower].coll.add(pendingWStETHReward);
        uint currentBaseFeeLMADebt = Troves[_borrower].debt.add(
            pendingBaseFeeLMADebtReward
        );

        return (currentWStETH, currentBaseFeeLMADebt);
    }

    function applyPendingRewards(address _borrower) external {
        _requireCallerIsBorrowerOperations();
        return _applyPendingRewards(activePool, defaultPool, _borrower);
    }

    // Add the borrowers's coll and debt rewards earned from redistributions, to their Trove
    function _applyPendingRewards(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        address _borrower
    ) internal {
        if (hasPendingRewards(_borrower)) {
            _requireTroveIsActive(_borrower);

            // Compute pending rewards
            uint pendingWStETHReward = getPendingWStETHReward(_borrower);

            uint pendingBaseFeeLMADebtReward = getPendingBaseFeeLMADebtReward(
                _borrower
            );

            // Apply pending rewards to trove's state
            Troves[_borrower].coll = Troves[_borrower].coll.add(
                pendingWStETHReward
            );

            Troves[_borrower].debt = Troves[_borrower].debt.add(
                pendingBaseFeeLMADebtReward
            );

            _updateTroveRewardSnapshots(_borrower);

            // Transfer from DefaultPool to ActivePool
            _movePendingTroveRewardsToActivePool(
                _activePool,
                _defaultPool,
                pendingBaseFeeLMADebtReward,
                pendingWStETHReward
            );

            emit TroveUpdated(
                _borrower,
                Troves[_borrower].debt,
                Troves[_borrower].coll,
                Troves[_borrower].stake,
                TroveManagerOperation.applyPendingRewards
            );
        }
    }

    // Update borrower's snapshots of L_WStETH and L_BaseFeeLMADebt to reflect the current values
    function updateTroveRewardSnapshots(address _borrower) external {
        _requireCallerIsBorrowerOperations();
        return _updateTroveRewardSnapshots(_borrower);
    }

    function _updateTroveRewardSnapshots(address _borrower) internal {
        rewardSnapshots[_borrower].WStETH = L_WStETH;
        rewardSnapshots[_borrower].BaseFeeLMADebt = L_BaseFeeLMADebt;
        emit TroveSnapshotsUpdated(L_WStETH, L_BaseFeeLMADebt);
    }

    // Get the borrower's pending accumulated WStETH reward, earned by their stake
    function getPendingWStETHReward(
        address _borrower
    ) public view returns (uint) {
        uint snapshotWStETH = rewardSnapshots[_borrower].WStETH;
        uint rewardPerUnitStaked = L_WStETH.sub(snapshotWStETH);

        if (
            rewardPerUnitStaked == 0 ||
            Troves[_borrower].status != Status.active
        ) {
            return 0;
        }

        uint stake = Troves[_borrower].stake;

        uint pendingWStETHReward = stake.mul(rewardPerUnitStaked).div(
            DECIMAL_PRECISION
        );

        return pendingWStETHReward;
    }

    // Get the borrower's pending accumulated BaseFeeLMA reward, earned by their stake
    function getPendingBaseFeeLMADebtReward(
        address _borrower
    ) public view returns (uint) {
        uint snapshotBaseFeeLMADebt = rewardSnapshots[_borrower].BaseFeeLMADebt;
        uint rewardPerUnitStaked = L_BaseFeeLMADebt.sub(snapshotBaseFeeLMADebt);

        if (
            rewardPerUnitStaked == 0 ||
            Troves[_borrower].status != Status.active
        ) {
            return 0;
        }

        uint stake = Troves[_borrower].stake;

        uint pendingBaseFeeLMADebtReward = stake.mul(rewardPerUnitStaked).div(
            DECIMAL_PRECISION
        );

        return pendingBaseFeeLMADebtReward;
    }

    function hasPendingRewards(address _borrower) public view returns (bool) {
        /*
         * A Trove has pending rewards if its snapshot is less than the current rewards per-unit-staked sum:
         * this indicates that rewards have occured since the snapshot was made, and the user therefore has
         * pending rewards
         */
        if (Troves[_borrower].status != Status.active) {
            return false;
        }

        return (rewardSnapshots[_borrower].WStETH < L_WStETH);
    }

    // Return the Troves entire debt and coll, including pending rewards from redistributions.
    function getEntireDebtAndColl(
        address _borrower
    )
        public
        view
        returns (
            uint debt,
            uint coll,
            uint pendingBaseFeeLMADebtReward,
            uint pendingWStETHReward
        )
    {
        debt = Troves[_borrower].debt;
        coll = Troves[_borrower].coll;

        pendingBaseFeeLMADebtReward = getPendingBaseFeeLMADebtReward(_borrower);
        pendingWStETHReward = getPendingWStETHReward(_borrower);

        debt = debt.add(pendingBaseFeeLMADebtReward);
        coll = coll.add(pendingWStETHReward);
    }

    function removeStake(address _borrower) external {
        _requireCallerIsBorrowerOperations();
        return _removeStake(_borrower);
    }

    // Remove borrower's stake from the totalStakes sum, and set their stake to 0
    function _removeStake(address _borrower) internal {
        uint stake = Troves[_borrower].stake;
        totalStakes = totalStakes.sub(stake);
        Troves[_borrower].stake = 0;
    }

    function updateStakeAndTotalStakes(
        address _borrower
    ) external returns (uint) {
        _requireCallerIsBorrowerOperations();
        return _updateStakeAndTotalStakes(_borrower);
    }

    // Update borrower's stake based on their latest collateral value
    function _updateStakeAndTotalStakes(
        address _borrower
    ) internal returns (uint) {
        uint newStake = _computeNewStake(Troves[_borrower].coll);
        uint oldStake = Troves[_borrower].stake;
        Troves[_borrower].stake = newStake;

        totalStakes = totalStakes.sub(oldStake).add(newStake);
        emit TotalStakesUpdated(totalStakes);

        return newStake;
    }

    // Calculate a new stake based on the snapshots of the totalStakes and totalCollateral taken at the last liquidation
    function _computeNewStake(uint _coll) internal view returns (uint) {
        uint stake;
        if (totalCollateralSnapshot == 0) {
            stake = _coll;
        } else {
            /*
             * The following assert() holds true because:
             * - The system always contains >= 1 trove
             * - When we close or liquidate a trove, we redistribute the pending rewards, so if all troves were closed/liquidated,
             * rewards wouldve been emptied and totalCollateralSnapshot would be zero too.
             */
            assert(totalStakesSnapshot > 0);
            stake = _coll.mul(totalStakesSnapshot).div(totalCollateralSnapshot);
        }
        return stake;
    }

    function _redistributeDebtAndColl(
        IActivePool _activePool,
        IDefaultPool _defaultPool,
        uint _debt,
        uint _coll
    ) internal {
        if (_debt == 0) {
            return;
        }

        /*
         * Add distributed coll and debt rewards-per-unit-staked to the running totals. Division uses a "feedback"
         * error correction, to keep the cumulative error low in the running totals L_WStETH and L_BaseFeeLMADebt:
         *
         * 1) Form numerators which compensate for the floor division errors that occurred the last time this
         * function was called.
         * 2) Calculate "per-unit-staked" ratios.
         * 3) Multiply each ratio back by its denominator, to reveal the current floor division error.
         * 4) Store these errors for use in the next correction when this function is called.
         * 5) Note: static analysis tools complain about this "division before multiplication", however, it is intended.
         */
        uint WStETHNumerator = _coll.mul(DECIMAL_PRECISION).add(
            lastWStETHError_Redistribution
        );
        uint BaseFeeLMADebtNumerator = _debt.mul(DECIMAL_PRECISION).add(
            lastBaseFeeLMADebtError_Redistribution
        );

        // Get the per-unit-staked terms
        uint WStETHRewardPerUnitStaked = WStETHNumerator.div(totalStakes);
        uint BaseFeeLMADebtRewardPerUnitStaked = BaseFeeLMADebtNumerator.div(
            totalStakes
        );

        lastWStETHError_Redistribution = WStETHNumerator.sub(
            WStETHRewardPerUnitStaked.mul(totalStakes)
        );
        lastBaseFeeLMADebtError_Redistribution = BaseFeeLMADebtNumerator.sub(
            BaseFeeLMADebtRewardPerUnitStaked.mul(totalStakes)
        );

        // Add per-unit-staked terms to the running totals
        L_WStETH = L_WStETH.add(WStETHRewardPerUnitStaked);
        L_BaseFeeLMADebt = L_BaseFeeLMADebt.add(
            BaseFeeLMADebtRewardPerUnitStaked
        );

        emit LTermsUpdated(L_WStETH, L_BaseFeeLMADebt);

        // Transfer coll and debt from ActivePool to DefaultPool
        _activePool.decreaseBaseFeeLMADebt(_debt);
        _defaultPool.increaseBaseFeeLMADebt(_debt);
        _defaultPool.increaseBalance(_coll);
        _activePool.sendWStETH(address(_defaultPool), _coll);
    }

    function closeTrove(address _borrower) external {
        _requireCallerIsBorrowerOperations();
        return _closeTrove(_borrower, Status.closedByOwner);
    }

    function _closeTrove(address _borrower, Status closedStatus) internal {
        assert(
            closedStatus != Status.nonExistent && closedStatus != Status.active
        );

        uint TroveOwnersArrayLength = TroveOwners.length;
        _requireMoreThanOneTroveInSystem(TroveOwnersArrayLength);

        Troves[_borrower].status = closedStatus;
        Troves[_borrower].coll = 0;
        Troves[_borrower].debt = 0;

        rewardSnapshots[_borrower].WStETH = 0;
        rewardSnapshots[_borrower].BaseFeeLMADebt = 0;

        _removeTroveOwner(_borrower, TroveOwnersArrayLength);
        sortedTroves.remove(_borrower);
    }

    /*
     * Updates snapshots of system total stakes and total collateral, excluding a given collateral remainder from the calculation.
     * Used in a liquidation sequence.
     *
     * The calculation excludes a portion of collateral that is in the ActivePool:
     *
     * the total WStETH gas compensation from the liquidation sequence
     *
     * The WStETH as compensation must be excluded as it is always sent out at the very end of the liquidation sequence.
     */
    function _updateSystemSnapshots_excludeCollRemainder(
        IActivePool _activePool,
        uint _collRemainder
    ) internal {
        totalStakesSnapshot = totalStakes;

        uint activeColl = _activePool.getWStETH();
        uint liquidatedColl = defaultPool.getWStETH();
        totalCollateralSnapshot = activeColl.sub(_collRemainder).add(
            liquidatedColl
        );

        emit SystemSnapshotsUpdated(
            totalStakesSnapshot,
            totalCollateralSnapshot
        );
    }

    // Push the owner's address to the Trove owners list, and record the corresponding array index on the Trove struct
    function addTroveOwnerToArray(
        address _borrower
    ) external returns (uint index) {
        _requireCallerIsBorrowerOperations();
        return _addTroveOwnerToArray(_borrower);
    }

    function _addTroveOwnerToArray(
        address _borrower
    ) internal returns (uint128 index) {
        /* Max array size is 2**128 - 1, i.e. ~3e30 troves. No risk of overflow, since troves have minimum BaseFeeLMA
        debt of liquidation reserve plus MIN_NET_DEBT. 3e30 BaseFeeLMA dwarfs the value of all wealth in the world ( which is < 1e15 USD). */

        // Push the Troveowner to the array
        TroveOwners.push(_borrower);

        // Record the index of the new Troveowner on their Trove struct
        index = uint128(TroveOwners.length.sub(1));
        Troves[_borrower].arrayIndex = index;

        return index;
    }

    /*
     * Remove a Trove owner from the TroveOwners array, not preserving array order. Removing owner 'B' does the following:
     * [A B C D E] => [A E C D], and updates E's Trove struct to point to its new array index.
     */
    function _removeTroveOwner(
        address _borrower,
        uint TroveOwnersArrayLength
    ) internal {
        Status troveStatus = Troves[_borrower].status;
        // Its set in caller function `_closeTrove`
        assert(
            troveStatus != Status.nonExistent && troveStatus != Status.active
        );

        uint128 index = Troves[_borrower].arrayIndex;
        uint length = TroveOwnersArrayLength;
        uint idxLast = length.sub(1);

        assert(index <= idxLast);

        address addressToMove = TroveOwners[idxLast];

        TroveOwners[index] = addressToMove;
        Troves[addressToMove].arrayIndex = index;
        emit TroveIndexUpdated(addressToMove, index);

        TroveOwners.pop();
    }

    // --- Recovery Mode and TCR functions ---

    function getTCR(uint _price) external view returns (uint) {
        return _getTCR(_price);
    }

    /**
     * HEDGEHOG UPDATES:
     * New view method to help with getting the data on frontends
     */
    function getUnreliableTCR() external view returns (uint) {
        return _getTCR(priceFeed.lastGoodPrice());
    }

    function checkRecoveryMode(uint _price) external view returns (bool) {
        return _checkRecoveryMode(_price);
    }

    /**
     * HEDGEHOG UPDATES:
     * New view method to help with getting the data on frontends
     */
    function checkUnreliableRecoveryMode() external view returns (bool) {
        return _checkRecoveryMode(priceFeed.lastGoodPrice());
    }

    // Check whether or not the system *would be* in Recovery Mode, given an WStETH:USD price, and the entire system coll and debt.
    function _checkPotentialRecoveryMode(
        uint _entireSystemColl,
        uint _entireSystemDebt,
        uint _price
    ) internal view returns (bool) {
        uint TCR = LiquityMath._computeCR(
            _entireSystemColl,
            _entireSystemDebt,
            _price
        );

        return TCR < CCR;
    }

    // --- Redemption fee functions ---

    /*
     * HEDGEHOG UPDATES:
     * 1) Rename variable in docs (baseRate => redemptionBaseRate)
     * 2) decayedRemeptionBaseRate (decayedBaseRate) is now calculated by _calcDecayedRedemptionBaseRate();
     * 3) Updating RedemptionBaseRate state variable instead of baseRate
     * 4) Emiting RedemptionBaseRateUpdated instead of BaseRateUpdates();
     * 5) Now updates time only of redemeption operation instead of both redemption and borrow
     *
     * This function has two impacts on the redemptionBaseRate state variable:
     * 1) decays the redemptionBaseRate based on time passed since last redemption or BaseFeeLMA borrowing operation.
     * then,
     * 2) increases the redemptionBaseRate based on the amount redeemed, as a proportion of total supply
     */
    function _updateRedemptionBaseRateFromRedemption(
        uint _WStETHDrawn
    ) internal returns (uint) {
        uint decayedRedemptionBaseRate = _calcDecayedRedemptionBaseRate();
        // Hedgehog updates: Now calculating what part of total collateral is getting withdrawn from the
        // system

        // HEDGEHOG UPDATES: not dividing, but multyplying by decimal precision
        /* Convert the drawn WStETH back to BaseFeeLMA at face value rate (1 BaseFeeLMA:1 USD), in order to get
         * the fraction of total supply that was redeemed at face value. */
        uint redeemedBaseFeeLMAFraction = _WStETHDrawn
            .mul(DECIMAL_PRECISION)
            .div(activePool.getWStETH());

        // Hedgehog Updates: Remove division by BETA
        uint newBaseRate = decayedRedemptionBaseRate.add(
            redeemedBaseFeeLMAFraction
        );

        newBaseRate = LiquityMath._min(newBaseRate, DECIMAL_PRECISION); // cap baseRate at a maximum of 100%
        //assert(newBaseRate <= DECIMAL_PRECISION); // This is already enforced in the line above
        assert(newBaseRate > 0); // Base rate is always non-zero after redemption

        // HEDGEHOG UPDATES: succesful redemption now updates only the redemption base rate. Redemption base rate update also received a new event.
        // Update the baseRate state variable
        redemptionBaseRate = newBaseRate;
        emit RedemptionBaseRateUpdated(newBaseRate);

        _updateLastRedemptionTime();
        return newBaseRate;
    }

    /*
     * HEDGEHOG UPDATES:
     * 1) Now passing redemptionBaseRate instead of combined baseRate
     * 2) Now accepts a new param: redemptionColl as we can't get that amount from value anymore since of ERC20 transition
     */
    function getRedemptionRate(
        uint _redemptionColl
    ) public view returns (uint) {
        return _calcRedemptionRate(redemptionBaseRate, _redemptionColl);
    }

    /*
     * HEDGEHOG UPDATES:
     * Now accepts a new param: redemptionColl as we can't get that amount from value anymore since of ERC20 transition
     */
    function getRedemptionRateWithDecay(
        uint _redemptionColl
    ) public view returns (uint) {
        return
            _calcRedemptionRate(
                _calcDecayedRedemptionBaseRate(),
                _redemptionColl
            );
    }

    /*
     * HEDGEHOG UPDATES:
     * Redemption Rate formula now is: RedFloor + RedBaseRate*MinuteDecayFactorMinutes + RedemptionETH/TotalColl
     * 1) Rename param name (_baseRate => _redemptionBaseRate)
     * 2) Now redeemed collateral divided by total collateral in active pool is added to the sum of redemption floor and redeem base rate
     */
    function _calcRedemptionRate(
        uint _redemptionBaseRate,
        uint _redemptionColl
    ) internal view returns (uint) {
        return
            LiquityMath._min(
                REDEMPTION_FEE_FLOOR.add(_redemptionBaseRate).add(
                    _redemptionColl.div(
                        activePool.getWStETH() + defaultPool.getWStETH()
                    )
                ),
                DECIMAL_PRECISION // cap at a maximum of 100%
            );
    }

    function _getRedemptionFee(uint _WStETHDrawn) internal view returns (uint) {
        return
            _calcRedemptionFee(getRedemptionRate(_WStETHDrawn), _WStETHDrawn);
    }

    function getRedemptionFeeWithDecay(
        uint _WStETHDrawn
    ) external view returns (uint) {
        return
            _calcRedemptionFee(
                getRedemptionRateWithDecay(_WStETHDrawn),
                _WStETHDrawn
            );
    }

    function _calcRedemptionFee(
        uint _redemptionRate,
        uint _WStETHDrawn
    ) internal pure returns (uint) {
        uint redemptionFee = _redemptionRate.mul(_WStETHDrawn).div(
            DECIMAL_PRECISION
        );

        // Hedgehog Updates: check if fee is too big is now performed at the BO contract

        return redemptionFee;
    }

    // --- Borrowing fee functions ---

    /*
     * HEDGEHOG UPDATES:
     * 1) Now passing borrowBaseRate instead of combined baseRate
     */
    function getBorrowingRate(
        uint _issuedBaseFeeLMA
    ) public view returns (uint) {
        return _calcBorrowingRate(borrowBaseRate, _issuedBaseFeeLMA);
    }

    /*
     * HEDGEHOG UPDATES:
     * 1) Now passing _calcDecayedBorrowBaseRate instead of _calcDecayedBaseRate function to calculate the decayed borrowBaseRate
     */
    function getBorrowingRateWithDecay(
        uint _issuedBaseFeeLMA
    ) public view returns (uint) {
        return
            _calcBorrowingRate(
                _calcDecayedRedemptionBaseRate(),
                _issuedBaseFeeLMA
            );
    }

    /*
     * HEDGEHOG UPDATES:
     * Now full dynamic fees formula is as follows: RedRate = RedFloor + RedBaseRate*MinuteDecayFactorMinutes + RedemptionETH / Total Collateral in the system
     * 1) Rename param name (_baseRate => _borrowBaseRate)
     * 2) If BFE total supply is 0, returning fee floor
     * 3) Now adding issued bfe amount divided by total supply of the asset to the sum of borrow floor and decayed borrowedBaseRate
     */
    function _calcBorrowingRate(
        uint _borrowBaseRate,
        uint _issuedBaseFeeLMA
    ) internal view returns (uint) {
        uint256 supply = baseFeeLMAToken.totalSupply();
        // Checking if there are tokens in supply, otherwise return 1 to avoid division by zero
        if (supply == 0) {
            return BORROWING_FEE_FLOOR;
        }

        return
            LiquityMath._min(
                BORROWING_FEE_FLOOR.add(_borrowBaseRate).add(
                    _issuedBaseFeeLMA.mul(DECIMAL_PRECISION).div(supply)
                ),
                MAX_BORROWING_FEE
            );
    }

    // HEDGEHOG UPDATES: Now retuns also a calculated base rate along with a borrowing fee
    function getBorrowingFee(
        uint _BaseFeeLMADebt
    ) external view returns (uint, uint) {
        uint baseRate = getBorrowingRate(_BaseFeeLMADebt);
        return (_calcBorrowingFee(baseRate, _BaseFeeLMADebt), baseRate);
    }

    function getBorrowingFeeWithDecay(
        uint _BaseFeeLMADebt
    ) external view returns (uint) {
        return
            _calcBorrowingFee(
                getBorrowingRateWithDecay(_BaseFeeLMADebt),
                _BaseFeeLMADebt
            );
    }

    function _calcBorrowingFee(
        uint _borrowingRate,
        uint _BaseFeeLMADebt
    ) internal pure returns (uint) {
        return _borrowingRate.mul(_BaseFeeLMADebt).div(DECIMAL_PRECISION);
    }

    // HEDGEHOG UPDATES: New function to updtae borrowBaseRate during borrowing op on BorrowersOperations contract
    function updateBaseRateFromBorrowing(uint _newBaseRate) external {
        require(
            msg.sender == borrowerOperationsAddress,
            "TroveManager: Only Borrower operations may call"
        );
        if (_newBaseRate >= BORROWING_FEE_FLOOR) {
            borrowBaseRate = _newBaseRate - BORROWING_FEE_FLOOR;
        } else {
            borrowBaseRate = 0;
        }

        emit BorrowBaseRateUpdated(_newBaseRate);
    }

    /*
     * HEDGEHOG UPDATES:
     * 1) Now updates borrowBaseRate instead of baseRate used by both redemption and minting functions
     * 2) Emit BorrowBaseRateUpdated instead of BaseRateUpdated
     * 3) Now updates time only of borrow operation instead of both redemption and borrow
     * 4) Update doc variable name baseRate => borrowBaseRate
     */
    // Updates the borrowBaseRate state variable based on time elapsed since the last redemption or BaseFeeLMA borrowing operation.
    function decayBaseRateFromBorrowing() external {
        _requireCallerIsBorrowerOperations();
        uint decayedBaseRate = _calcDecayedBorrowBaseRate();
        assert(decayedBaseRate <= DECIMAL_PRECISION); // The baseRate can decay to 0
        // HEDGEHOG LOGIC CHANGES: Updating a unique borrowing base rate instead of just "baseRate"
        borrowBaseRate = decayedBaseRate;

        emit BorrowBaseRateUpdated(decayedBaseRate);

        _updateLastBorrowTime();
    }

    // --- Internal fee functions ---

    /*
     * HEDGEHOG UPDATES:
     * removed _updateLastFeeOpTime
     * New function _updateLastRedemptionTime simmilar to _updateLastFeeOpTime, that sets lastRedemptionTime and emits respective event.
     */
    // Update the last fee operation time only if time passed >= decay interval. This prevents base rate griefing.
    function _updateLastRedemptionTime() internal {
        uint timePassed = block.timestamp.sub(lastRedemptionTime);

        if (timePassed >= SECONDS_IN_ONE_MINUTE) {
            lastRedemptionTime = block.timestamp;
            emit LastRedemptionTimeUpdated(block.timestamp);
        }
    }

    /*
     * HEDGEHOG UPDATES:
     * removed _updateLastFeeOpTime
     * New function _updateLastBorrowTime simmilar to _updateLastFeeOpTime, that sets lastBorrowTime and emits respective event.
     */
    // Update the last fee operation time only if time passed >= decay interval. This prevents base rate griefing.
    function _updateLastBorrowTime() internal {
        uint timePassed = block.timestamp.sub(lastBorrowTime);

        if (timePassed >= SECONDS_IN_ONE_MINUTE) {
            lastBorrowTime = block.timestamp;
            emit LastBorrowTimeUpdated(block.timestamp);
        }
    }

    /*
     * HEDGEHOG UPDATES:
     * New function simmilar to _calcDecayedBaseRate. However used particularly for redemptionBaseRate calculation
     */
    function _calcDecayedRedemptionBaseRate() internal view returns (uint) {
        uint minutesPassed = _minutesPassedSinceLastRedemption();

        uint decayFactor = LiquityMath._decPow(
            MINUTE_DECAY_REDEMPTION_FACTOR,
            minutesPassed
        );
        return redemptionBaseRate.mul(decayFactor).div(DECIMAL_PRECISION);
    }

    /*
     * HEDGEHOG UPDATES:
     * New function simmilar to _calcDecayedBaseRate. However used particularly for borrowBaseRate calculation
     */
    function _calcDecayedBorrowBaseRate() internal view returns (uint) {
        uint minutesPassed = _minutesPassedSinceLastBorrow();
        uint decayFactor = LiquityMath._decPow(
            MINUTE_DECAY_BORROWING_FACTOR,
            minutesPassed
        );

        return borrowBaseRate.mul(decayFactor).div(DECIMAL_PRECISION);
    }

    /*
     * HEDGEHOG UPDATES:
     * removed _minutesPassedSinceLastFeeOp
     * New function _minutesPassedSinceLastRedemption simmilar to _minutesPassedSinceLastFeeOp, that returns amount of minutes since last registered redemption
     */
    function _minutesPassedSinceLastRedemption() internal view returns (uint) {
        return
            (block.timestamp.sub(lastRedemptionTime)).div(
                SECONDS_IN_ONE_MINUTE
            );
    }

    /*
     * HEDGEHOG UPDATES:
     * removed _minutesPassedSinceLastFeeOp
     * New function _minutesPassedSinceLastBorrow simmilar to _minutesPassedSinceLastFeeOp, that returns amount of minutes since last registered borrow
     */
    function _minutesPassedSinceLastBorrow() internal view returns (uint) {
        return (block.timestamp.sub(lastBorrowTime)).div(SECONDS_IN_ONE_MINUTE);
    }

    // --- 'require' wrapper functions ---

    function _requireCallerIsBorrowerOperations() internal view {
        require(
            msg.sender == borrowerOperationsAddress,
            "TroveManager: Caller is not the BorrowerOperations contract"
        );
    }

    function _requireTroveIsActive(address _borrower) internal view {
        require(
            Troves[_borrower].status == Status.active,
            "TroveManager: Trove does not exist or is closed"
        );
    }

    function _requireBaseFeeLMABalanceCoversRedemption(
        IBaseFeeLMAToken _baseFeeLMAToken,
        address _redeemer,
        uint _amount
    ) internal view {
        require(
            _baseFeeLMAToken.balanceOf(_redeemer) >= _amount,
            "TroveManager: Requested redemption amount must be <= user's BaseFeeLMA token balance"
        );
    }

    function _requireMoreThanOneTroveInSystem(
        uint TroveOwnersArrayLength
    ) internal view {
        require(
            TroveOwnersArrayLength > 1 && sortedTroves.getSize() > 1,
            "TroveManager: Only one trove in the system"
        );
    }

    function _requireAmountGreaterThanZero(uint _amount) internal pure {
        require(_amount > 0, "TroveManager: Amount must be greater than zero");
    }

    function _requireTCRoverMCR(uint _price) internal view {
        require(
            _getTCR(_price) >= MCR,
            "TroveManager: Cannot redeem when TCR < MCR"
        );
    }

    function _requireAfterBootstrapPeriod() internal view {
        require(
            block.timestamp >= SYSTEM_DEPLOYMENT_TIME.add(BOOTSTRAP_PERIOD),
            "TroveManager: Redemptions are not allowed during bootstrap phase"
        );
    }

    function _requireValidMaxFeePercentage(
        uint _maxFeePercentage
    ) internal pure {
        require(
            _maxFeePercentage >= REDEMPTION_FEE_FLOOR &&
                _maxFeePercentage <= DECIMAL_PRECISION,
            "Max fee percentage must be between 0.5% and 100%"
        );
    }

    // --- Trove property getters ---

    function getTroveStatus(address _borrower) external view returns (uint) {
        return uint(Troves[_borrower].status);
    }

    function getTroveStake(address _borrower) external view returns (uint) {
        return Troves[_borrower].stake;
    }

    function getTroveDebt(address _borrower) external view returns (uint) {
        return Troves[_borrower].debt;
    }

    function getTroveColl(address _borrower) external view returns (uint) {
        return Troves[_borrower].coll;
    }

    // --- Trove property setters, called by BorrowerOperations ---

    function setTroveStatus(address _borrower, uint _num) external {
        _requireCallerIsBorrowerOperations();
        Troves[_borrower].status = Status(_num);
    }

    function increaseTroveColl(
        address _borrower,
        uint _collIncrease
    ) external returns (uint) {
        _requireCallerIsBorrowerOperations();
        uint newColl = Troves[_borrower].coll.add(_collIncrease);
        Troves[_borrower].coll = newColl;
        return newColl;
    }

    function decreaseTroveColl(
        address _borrower,
        uint _collDecrease
    ) external returns (uint) {
        _requireCallerIsBorrowerOperations();
        uint newColl = Troves[_borrower].coll.sub(_collDecrease);
        Troves[_borrower].coll = newColl;
        return newColl;
    }

    function increaseTroveDebt(
        address _borrower,
        uint _debtIncrease
    ) external returns (uint) {
        _requireCallerIsBorrowerOperations();
        uint newDebt = Troves[_borrower].debt.add(_debtIncrease);
        Troves[_borrower].debt = newDebt;
        return newDebt;
    }

    function decreaseTroveDebt(
        address _borrower,
        uint _debtDecrease
    ) external returns (uint) {
        _requireCallerIsBorrowerOperations();
        uint newDebt = Troves[_borrower].debt.sub(_debtDecrease);
        Troves[_borrower].debt = newDebt;
        return newDebt;
    }

    /*
     * HEDGEHOG UPDATES:
     * removed _minutesPassedSinceLastFeeOp
     * New function _minutesPassedSinceLastRedemption simmilar to _minutesPassedSinceLastFeeOp, that returns amount of minutes since last registered redemption
     */
    function getNormalLiquidationPrice(
        uint256 _coll,
        uint256 _debt
    ) external pure returns (uint256) {
        uint256 price = LiquityMath._findPriceBelowMCR(
            _coll,
            _debt,
            20,
            HedgehogBase.MCR
        );
        return price;
    }

    /*
     * HEDGEHOG UPDATES:
     * removed _minutesPassedSinceLastFeeOp
     * New function _minutesPassedSinceLastRedemption simmilar to _minutesPassedSinceLastFeeOp, that returns amount of minutes since last registered redemption
     */
    function getRecoveryLiquidationPrice(
        uint256 _coll,
        uint256 _debt
    ) external pure returns (uint256) {
        uint256 price = LiquityMath._findPriceBelowMCR(
            _coll,
            _debt,
            20,
            HedgehogBase._100pct
        );
        return price;
    }
